import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, Loader, Zap, Package, Home, List, ArrowRight, User, KeyRound, Phone, Mail, UserPlus, X, AlertTriangle, CheckCircle, Info, LogOut } from 'lucide-react';

// URL base da API do Backend (Node.js)
const API_BASE_URL = 'http://localhost:3000/api';

// --- Componente de Toast (Mensagens de Notificação) ---

const ToastMessage = ({ message, type, onClose }) => {
    if (!message) return null;

    const baseClasses = "fixed bottom-5 right-5 p-4 rounded-xl shadow-xl text-white transition-opacity duration-300 z-50 flex items-center";
    let typeClasses = '';
    let Icon = Info;

    switch (type) {
        case 'success':
            typeClasses = 'bg-green-600';
            Icon = CheckCircle;
            break;
        case 'error':
            typeClasses = 'bg-red-600';
            Icon = AlertTriangle;
            break;
        default:
            // Cor Padrão (Info)
            typeClasses = 'bg-gray-600'; 
            Icon = Info;
    }

    return (
        <div className={`${baseClasses} ${typeClasses}`} role="alert">
            <Icon className="w-5 h-5 mr-3" />
            <span className="font-semibold text-sm">{message}</span>
            <button onClick={onClose} className="ml-4 text-white opacity-70 hover:opacity-100 transition">
                <X className="w-5 h-5" />
            </button>
        </div>
    );
};

// --- Componente de Catálogo de Produtos ---

const CatalogoPage = ({ token }) => {
    const [produtos, setProdutos] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [isLoading, setIsLoading] = useState(true);

    // Efeito para carregar produtos ao montar o componente
    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                // Adiciona o Token JWT ao cabeçalho da requisição
                const response = await axios.get(`${API_BASE_URL}/produtos`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                
                setProdutos(response.data);
            } catch (error) {
                console.error("Erro ao buscar produtos:", error);
                setProdutos([]); 
            } finally {
                setIsLoading(false);
            }
        };

        if (token) {
            fetchProdutos();
        } else {
            setIsLoading(false);
        }
    }, [token]);

    // Filtra os produtos com base no termo de busca
    const produtosFiltrados = produtos.filter(produto =>
        produto.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
        produto.marca.toLowerCase().includes(searchTerm.toLowerCase()) ||
        produto.codigo.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (isLoading) {
        return (
            <div className="flex justify-center items-center h-96">
                <Loader className="w-8 h-8 text-yellow-500 animate-spin mr-2" />
                <p className="text-lg text-gray-600">Carregando catálogo...</p>
            </div>
        );
    }

    // Verifica se os dados são simulados
    const isSimulated = produtos.length > 0 && produtos[0].nome === "Processador B450";

    return (
        <div className="p-8 max-w-7xl mx-auto">
            <h2 className="text-3xl font-bold text-gray-800 mb-6 flex items-center">
                <List className="w-6 h-6 mr-2 text-yellow-500" /> Catálogo de Produtos
            </h2>

            {isSimulated && (
                <div className="bg-yellow-100 border-l-4 border-yellow-600 text-yellow-800 p-4 mb-6 rounded-lg" role="alert">
                    <p className="font-bold">Aviso:</p>
                    <p className="text-sm">Os produtos exibidos são **dados simulados**. O Backend ainda aguarda as chaves da **Alterdata Bimer** para carregar o inventário em tempo real.</p>
                </div>
            )}

            <div className="mb-8 p-4 bg-white rounded-xl shadow-lg">
                <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                    <input
                        type="text"
                        placeholder="Buscar por nome, código ou marca..."
                        className="w-full p-3 pl-10 border border-gray-300 rounded-lg focus:ring-yellow-500 focus:border-yellow-500 transition"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {produtosFiltrados.length === 0 ? (
                    <p className="col-span-full text-center text-gray-500 text-xl py-10">
                        Nenhum produto encontrado para "{searchTerm}".
                    </p>
                ) : (
                    produtosFiltrados.map(produto => (
                        <div key={produto.id} className="bg-white p-5 rounded-xl shadow-lg hover:shadow-xl transition duration-300 border border-gray-100">
                            <h3 className="text-lg font-semibold text-gray-800 truncate mb-1">{produto.nome}</h3>
                            <p className="text-sm text-gray-500 mb-4">{produto.marca}</p>

                            <div className="flex justify-between items-end">
                                <span className="text-2xl font-bold text-yellow-600">
                                    {produto.preco_base.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}
                                </span>
                                <span className={`px-2 py-0.5 text-xs font-medium rounded-full ${produto.estoque > 10 ? 'bg-green-100 text-green-700' : produto.estoque > 0 ? 'bg-yellow-100 text-yellow-700' : 'bg-red-100 text-red-700'}`}>
                                    Estoque: {produto.estoque} {produto.estoque === 1 ? 'unidade' : 'unidades'}
                                </span>
                            </div>

                            <p className="text-xs text-gray-400 mt-3">Cód: {produto.codigo}</p>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

// --- Componente de Cadastro de Revendedor ---

const RevendedorRegistrationPage = ({ navigate, showToast }) => {
    const [formData, setFormData] = useState({
        nome: '',
        telefone: '',
        email: '',
        senha: '',
        confirmarSenha: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (formData.senha !== formData.confirmarSenha) {
            showToast('As senhas não coincidem. Por favor, verifique.', 'error');
            return;
        }

        try {
            const response = await axios.post(`${API_BASE_URL}/register`, {
                name: formData.nome,
                phone: formData.telefone,
                email: formData.email,
                password: formData.senha
            });

            if (response.status === 201) {
                showToast(response.data.message || 'Cadastro efetuado com sucesso! Use seu login.', 'success');
                setFormData({ nome: '', telefone: '', email: '', senha: '', confirmarSenha: '' }); 
                navigate('login');
            } else {
                showToast('Erro no cadastro. Tente novamente.', 'error');
            }
        } catch (error) {
            const errorMessage = error.response?.data?.message || 'Erro de conexão ou usuário já cadastrado.';
            showToast(errorMessage, 'error');
            console.error('Erro no cadastro:', error);
        }
    };

    const inputClasses = "w-full p-3 border border-gray-300 rounded-lg focus:ring-yellow-500 focus:border-yellow-500 transition";

    return (
        <div className="flex justify-center items-center p-4">
            <div className="w-full max-w-md bg-white p-8 rounded-xl shadow-2xl border-t-4 border-yellow-500">
                <h2 className="text-3xl font-bold text-gray-800 mb-6 flex items-center justify-center">
                    <UserPlus className="w-6 h-6 mr-3 text-yellow-500" /> Cadastro Revendedor
                </h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input type="text" name="nome" placeholder="Nome Completo" value={formData.nome} onChange={handleChange} required className={inputClasses} />
                    <input type="tel" name="telefone" placeholder="Número de Telefone" value={formData.telefone} onChange={handleChange} required className={inputClasses} />
                    <input type="email" name="email" placeholder="E-mail (Login)" value={formData.email} onChange={handleChange} required className={inputClasses} />
                    <input type="password" name="senha" placeholder="Senha" value={formData.senha} onChange={handleChange} required className={inputClasses} />
                    <input type="password" name="confirmarSenha" placeholder="Confirmar Senha" value={formData.confirmarSenha} onChange={handleChange} required className={inputClasses} />

                    <button
                        type="submit"
                        className="w-full py-3 mt-4 bg-gray-800 text-yellow-500 font-semibold rounded-lg hover:bg-gray-700 transition duration-200 shadow-md border-2 border-yellow-500"
                    >
                        Registrar Revendedor
                    </button>
                    <p className="text-center text-sm text-gray-500 pt-2">
                        Já tem conta? <button type="button" onClick={() => navigate('login')} className="text-gray-800 hover:text-gray-600 font-medium">Faça Login</button>
                    </p>
                </form>
            </div>
        </div>
    );
};

// --- Componente de Login ---

const LoginPage = ({ navigate, showToast, setCurrentUser, setToken }) => {
    const [formData, setFormData] = useState({ email: '', senha: '' });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post(`${API_BASE_URL}/login`, {
                email: formData.email,
                password: formData.senha
            });

            if (response.data && response.data.token) {
                const { token, user } = response.data;
                
                setCurrentUser(user);
                setToken(token);

                showToast(`Bem-vindo, ${user.name}!`, 'success');
                navigate('catalogo');
            } else {
                 showToast('Credenciais inválidas. Tente novamente.', 'error');
            }
        } catch (error) {
            const errorMessage = error.response?.data?.message || 'Erro de conexão. Verifique o Backend.';
            showToast(errorMessage, 'error');
            console.error('Erro no Login:', error.response?.data || error.message);
        }
    };
    
    const inputClasses = "w-full p-3 border border-gray-300 rounded-lg focus:ring-yellow-500 focus:border-yellow-500 transition";

    return (
        <div className="flex justify-center items-center p-4">
            <div className="w-full max-w-sm bg-white p-8 rounded-xl shadow-2xl border-t-4 border-gray-800">
                <h2 className="text-3xl font-bold text-gray-800 mb-6 flex items-center justify-center">
                    <KeyRound className="w-6 h-6 mr-3 text-gray-800" /> Acesso de Revendedor
                </h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input type="email" name="email" placeholder="E-mail (Login)" value={formData.email} onChange={handleChange} required className={inputClasses} />
                    <input type="password" name="senha" placeholder="Senha" value={formData.senha} onChange={handleChange} required className={inputClasses} />
                    
                    <button
                        type="submit"
                        className="w-full py-3 mt-4 bg-yellow-500 text-gray-800 font-semibold rounded-lg hover:bg-yellow-600 transition duration-200 shadow-md"
                    >
                        Entrar no Portal
                    </button>
                    <p className="text-center text-sm text-gray-500 pt-2">
                        Não é revendedor? <button type="button" onClick={() => navigate('register')} className="text-gray-800 hover:text-gray-600 font-medium">Cadastre-se aqui</button>
                    </p>
                </form>
            </div>
        </div>
    );
};

// --- Componente da Página Inicial ---

const HomePage = ({ navigate }) => {
    return (
        <div className="flex justify-center items-center h-full p-4">
            <div className="w-full max-w-xl bg-white p-10 rounded-xl shadow-2xl text-center border-t-8 border-yellow-500">
                <Home className="w-12 h-12 mx-auto text-yellow-500 mb-4" />
                <h2 className="text-4xl font-extrabold text-gray-800 mb-4">Bem-vindo ao Portal Ouro Branco</h2>
                <p className="text-lg text-gray-600 mb-8">
                    Acesse o catálogo em tempo real para consultar preços, códigos e disponibilidade de estoque, ou cadastre-se como nosso novo revendedor parceiro!
                </p>
                <div className="flex flex-col sm:flex-row gap-4 justify-center">
                    <button
                        onClick={() => navigate('catalogo')}
                        className="py-3 px-6 flex items-center justify-center bg-yellow-500 text-gray-800 font-semibold rounded-lg hover:bg-yellow-600 transition duration-200 shadow-lg transform hover:scale-[1.02] active:scale-[0.98]"
                    >
                        <Package className="w-5 h-5 mr-2" /> Catálogo de Produtos
                    </button>
                    <button
                        onClick={() => navigate('register')}
                        className="py-3 px-6 flex items-center justify-center bg-gray-800 text-white font-semibold rounded-lg hover:bg-gray-700 transition duration-200 shadow-lg transform hover:scale-[1.02] active:scale-[0.98]"
                    >
                        <UserPlus className="w-5 h-5 mr-2" /> Seja um Revendedor
                    </button>
                </div>
            </div>
        </div>
    );
};

// --- Componente Principal da Aplicação ---

const App = () => {
    const [currentPage, setCurrentPage] = useState('home');
    const [currentUser, setCurrentUser] = useState(null); // { id, name, email }
    const [token, setToken] = useState(null); // JWT Token
    const [toast, setToast] = useState({ message: '', type: '' });

    // Função para navegar e aplicar a proteção de rota
    const navigate = (page) => {
        if (page === 'catalogo' && !token) {
            showToast('Você precisa fazer login para acessar o catálogo.', 'error');
            setCurrentPage('login'); // Força para a página de login
            return;
        }
        setCurrentPage(page);
    };

    const showToast = (message, type = 'info') => {
        setToast({ message, type });
        setTimeout(() => setToast({ message: '', type: '' }), 4000);
    };

    const handleLogout = () => {
        setCurrentUser(null);
        setToken(null);
        navigate('home');
        showToast('Sessão encerrada com sucesso.', 'info');
    };
    
    // Renderiza o componente da página atual
    const renderPage = () => {
        switch (currentPage) {
            case 'catalogo':
                // Se, por alguma razão, chegar aqui sem token, redireciona para login (fallback de segurança)
                if (!token) return <LoginPage navigate={navigate} showToast={showToast} setCurrentUser={setCurrentUser} setToken={setToken} />;
                return <CatalogoPage token={token} />;
            case 'login':
                return <LoginPage navigate={navigate} showToast={showToast} setCurrentUser={setCurrentUser} setToken={setToken} />;
            case 'register':
                return <RevendedorRegistrationPage navigate={navigate} showToast={showToast} />;
            case 'home':
            default:
                return <HomePage navigate={navigate} />;
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col">
            {/* Header (Cabeçalho) - Preto e Laranja */}
            <header className="w-full bg-gray-900 text-white shadow-xl border-b-4 border-yellow-500">
                <div className="max-w-7xl mx-auto flex justify-between items-center p-4">
                    {/* Logo/Título */}
                    <div className="text-2xl font-extrabold text-yellow-500">
                        Portal Ouro Branco
                    </div>
                    
                    {/* Navegação */}
                    <nav className="flex space-x-4">
                        <button onClick={() => navigate('home')} className={`p-2 rounded-lg text-sm font-medium transition ${currentPage === 'home' ? 'bg-yellow-500 text-gray-900' : 'hover:bg-gray-700'}`}>
                            <Home className="w-5 h-5 inline-block mr-1" /> Início
                        </button>
                        <button 
                            onClick={() => navigate('catalogo')} 
                            className={`p-2 rounded-lg text-sm font-medium transition ${currentPage === 'catalogo' ? 'bg-yellow-500 text-gray-900' : 'hover:bg-gray-700'}`}
                        >
                            <List className="w-5 h-5 inline-block mr-1" /> Catálogo
                        </button>

                        {/* Botão de Acesso/Logout */}
                        {currentUser ? (
                            <div className="flex items-center space-x-2 bg-yellow-500 p-2 rounded-lg shadow-md text-gray-900 font-semibold">
                                <span className="text-sm truncate max-w-[100px]">Olá, {currentUser.name}!</span>
                                <button onClick={handleLogout} className="p-1 bg-gray-800 text-white rounded-lg hover:bg-gray-700 transition">
                                    <LogOut className="w-4 h-4" />
                                </button>
                            </div>
                        ) : (
                            <button onClick={() => navigate('login')} className={`p-2 rounded-lg text-sm font-medium transition bg-yellow-500 text-gray-900 hover:bg-yellow-600 shadow-md`}>
                                <User className="w-5 h-5 inline-block mr-1" /> Acesso
                            </button>
                        )}
                    </nav>
                </div>
            </header>
            
            {/* Conteúdo Principal */}
            <main className="flex-grow">
                {renderPage()}
            </main>

            {/* Componente de Notificação (Toast) */}
            <ToastMessage message={toast.message} type={toast.type} onClose={() => setToast({ message: '', type: '' })} />

        </div>
    );
};

export default App;
