// 3. CatalogScreen (Consome a API)
const CatalogScreen = ({ onViewChange }) => {
    const { products, loading, error } = useProductData();

    return (
        <div className="w-full max-w-6xl p-6 bg-white shadow-2xl rounded-2xl border-t-8 border-yellow-400">
            <div className="flex items-center justify-between mb-6 border-b pb-3">
                <h2 className="text-3xl font-bold text-black">Catálogo de Produtos</h2>
                <button
                    onClick={() => onViewChange('home')}
                    className="flex items-center text-blue-600 hover:text-blue-800 transition duration-150"
                >
                    <ArrowLeft className="w-4 h-4 mr-1" /> Voltar
                </button>
            </div>

            {loading && (
                <div className="flex flex-col items-center justify-center p-10 text-gray-500">
                    <Loader2 className="w-8 h-8 animate-spin mb-3" />
                    <p>Carregando dados do backend... ({API_BASE_URL}/produtos/index)</p>
                </div>
            )}

            {error && (
                <div className="p-6 bg-red-100 border border-red-400 text-red-700 rounded-lg">
                    <p className="font-bold">Erro ao carregar o Catálogo:</p>
                    <p className="text-sm">{error}</p>
                    <p className="mt-2 text-xs">Verifique se o seu servidor Node.js/Express está rodando na porta 3000 e se a rota `/api/produtos/index` está configurada corretamente.</p>
                </div>
            )}

            {!loading && products.length > 0 && (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {products.slice(0, 15).map((product, index) => ( // Limita a 15 para demonstração
                        <div key={index} className="bg-gray-50 p-4 rounded-xl border border-gray-200 hover:shadow-md transition duration-200">
                            <h3 className="text-lg font-semibold text-black mb-1">{product.nome}</h3>
                            <p className="text-sm text-gray-500">
                                Código: <span className="font-mono text-gray-700">{product.codigo}</span>
                            </p>
                            <div className="flex justify-between items-center mt-3 pt-3 border-t border-gray-200">
                                <span className="text-xl font-bold text-green-700">
                                    {formatCurrency(product.codigo)} {/* Preço mockado */}
                                </span>
                                <button className="flex items-center bg-yellow-500 text-black py-1 px-3 rounded-lg text-sm font-medium hover:bg-yellow-600 transition duration-150">
                                    Detalhes
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
            {!loading && !error && products.length === 0 && (
                <div className="p-6 text-center text-gray-500">
                    Nenhum produto encontrado. A lista `ListaObjetos` está vazia.
                </div>
            )}
        </div>
    );
};


// 4. Access Screen (Tela de Acesso/Login simplificada)
const AccessScreen = ({ onViewChange }) => (
    <div className="p-8 bg-white shadow-2xl rounded-2xl w-full max-w-md text-center border-t-8 border-yellow-400">
        <h2 className="text-2xl font-bold text-black mb-4">Acesso de Clientes</h2>
        <p className="text-gray-600 mb-6">Esta é uma tela de acesso simulada para autenticação.</p>
        <button
            onClick={() => onViewChange('home')}
            className="flex items-center justify-center w-full bg-black text-white py-3 rounded-lg font-semibold hover:bg-gray-800 transition duration-200"
        >
            <ArrowLeft className="w-4 h-4 mr-2" /> Voltar para Home
        </button>
    </div>
);


// === COMPONENTE PRINCIPAL (App) ===
const App = () => {
    // Gerenciamento de visualização (simula o roteamento)
    const [view, setView] = useState('home'); 

    const handleViewChange = useCallback((newView) => {
        setView(newView);
    }, []);

    // Função para renderizar o conteúdo central
    const renderContent = () => {
        switch (view) {
            case 'home':
                return <HomeScreen onViewChange={handleViewChange} />;
            case 'catalog':
                return <CatalogScreen onViewChange={handleViewChange} />;
            case 'access':
                return <AccessScreen onViewChange={handleViewChange} />;
            case 'history':
            case 'contact':
                return <div className="p-10 text-xl font-semibold text-gray-600">Página em Construção: {view.toUpperCase()}</div>
            default:
                return <HomeScreen onViewChange={handleViewChange} />;
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center font-sans">
            <Navbar onViewChange={handleViewChange} currentView={view} />
            
            <main className="flex-grow flex items-center justify-center p-4 w-full">
                {renderContent()}
            </main>

            <footer className="w-full bg-gray-800 p-4 text-center text-gray-400 text-sm mt-auto">
                © 2025 Ouro Branco. Todos os direitos reservados.
            </footer>
        </div>
    );
};

export default App;
