import React, { useState, useEffect, useMemo, useCallback } from 'react';
import axios from 'axios';
import { 
    Search, Package, Tag, DollarSign, Loader2, ArrowLeft, ArrowRight, X, 
    ShoppingCart, User, History, Phone, Mail, Truck, Users, Menu, Home, Lock 
} from 'lucide-react';

// --- PALETA DE CORES OURO BRANCO ---
const GOLD_PRIMARY = '#FFD700'; // Dourado
const BLACK_PRIMARY = '#1A1A1A'; // Preto para fundo e texto principal
const WHITE_PRIMARY = 'white'; // Branco

// URL do nosso servidor Node.js (backend)
const API_URL = 'http://localhost:3000/api/produtos'; 
const API_USERS_URL = 'http://localhost:3000/api/users'; // Endpoint mockado para usuários

// --- DADOS MOCKADOS PARA SIMULAR AUTENTICAÇÃO E CONTEÚDO ---
const CUSTOMER_TYPES = [
    'Instalador', 'Eletricista', 'Informática', 'Instalação de Segurança', 'Outro'
];
const EMPLOYEE_ROLES = [
    'Comercial', 'Vendas', 'Gerência', 'Financeiro'
];
const MOCK_HISTORICO = [
    { id: 1, data: '2024-10-15', total: 'R$ 850,50', status: 'Entregue', itens: 3 },
    { id: 2, data: '2024-09-28', total: 'R$ 2.120,00', status: 'Enviado', itens: 5 },
    { id: 3, data: '2024-09-01', total: 'R$ 350,99', status: 'Cancelado', itens: 1 },
];
const CONTACT_INFO = {
    telefone: '(XX) 9999-8888',
    email: 'atendimento@ourobranco.com.br',
    freteMinimo: 'R$ 500,00',
};


// --- FUNÇÃO AUXILIAR DE FORMATAÇÃO ---
const formatCurrency = (value) => {
    return value; 
};


// --- Componente: Menu Lateral do Carrinho (Drawer) ---
const CartDrawer = ({ cart, isCartOpen, toggleCart, handleCheckout }) => {
    const total = cart.reduce((sum, item) => sum + (parseFloat(item.preco.replace('R$ ', '').replace(',', '.')) * item.quantidade), 0);

    return (
        <>
            {/* Overlay */}
            {isCartOpen && (
                <div 
                    className="fixed inset-0 bg-gray-900 bg-opacity-70 z-40 transition-opacity duration-300"
                    onClick={toggleCart}
                ></div>
            )}
            
            {/* Drawer */}
            <div 
                className={`fixed top-0 right-0 w-full sm:w-96 bg-white h-full shadow-2xl z-50 transform transition-transform duration-300 ease-in-out
                    ${isCartOpen ? 'translate-x-0' : 'translate-x-full'}`
                }
            >
                <div className="p-6 h-full flex flex-col">
                    <div className="flex justify-between items-center border-b pb-4 mb-4">
                        <h2 className="text-2xl font-bold text-gray-900 flex items-center">
                            <ShoppingCart className="w-6 h-6 mr-2" style={{ color: BLACK_PRIMARY }} /> Carrinho
                        </h2>
                        <button onClick={toggleCart} className="text-gray-500 hover:text-gray-900 p-1 rounded-full bg-gray-100 hover:bg-gray-200">
                            <X className="w-6 h-6" />
                        </button>
                    </div>

                    <div className="flex-grow overflow-y-auto space-y-4">
                        {cart.length === 0 ? (
                            <div className="text-center p-10 text-gray-500">
                                Seu carrinho está vazio.
                            </div>
                        ) : (
                            cart.map((item, index) => (
                                <div key={index} className="flex justify-between items-center p-3 rounded-lg shadow-sm" style={{ backgroundColor: '#FDFDD0', borderLeft: `4px solid ${GOLD_PRIMARY}` }}>
                                    <div>
                                        <p className="font-medium text-gray-900">{item.nome}</p>
                                        <p className="text-sm text-gray-700">Qtd: {item.quantidade} x {item.preco}</p>
                                    </div>
                                    <p className="font-bold text-lg" style={{ color: BLACK_PRIMARY }}>
                                        {formatCurrency(`R$ ${(parseFloat(item.preco.replace('R$ ', '').replace(',', '.')) * item.quantidade).toFixed(2).replace('.', ',')}`)}
                                    </p>
                                </div>
                            ))
                        )}
                    </div>

                    <div className="mt-4 border-t pt-4">
                        <div className="flex justify-between items-center text-xl font-bold mb-4">
                            <span>Total:</span>
                            <span className="text-3xl" style={{ color: GOLD_PRIMARY }}>{formatCurrency(`R$ ${total.toFixed(2).replace('.', ',')}`)}</span>
                        </div>
                        
                        <div className="bg-gray-100 text-gray-800 text-sm p-3 rounded-lg mb-4 flex items-center border border-gray-300">
                            <Truck className="w-5 h-5 mr-2" style={{ color: GOLD_PRIMARY }} />
                            <p>Frete mínimo de compras para isenção: {CONTACT_INFO.freteMinimo}</p>
                        </div>
                        
                        <button 
                            onClick={handleCheckout} 
                            disabled={cart.length === 0}
                            style={{ backgroundColor: BLACK_PRIMARY, color: GOLD_PRIMARY }}
                            className={`w-full py-3 rounded-xl font-bold transition duration-300 
                                ${cart.length === 0 ? 'bg-gray-400 cursor-not-allowed' : 'shadow-lg hover:shadow-xl'}`}
                        >
                            Finalizar Compra
                        </button>
                    </div>
                </div>
            </div>
        </>
    );
};


// --- Componente: Detalhe do Grupo (Lista de Produtos) ---
const GrupoDetalhe = ({ grupo, onBack, handleAddToCart }) => {
    const [produtosDoGrupo, setProdutosDoGrupo] = useState([]);
    const [loadingDetalhe, setLoadingDetalhe] = useState(true);
    const [errorDetalhe, setErrorDetalhe] = useState(null); 

    useEffect(() => {
        const fetchDetalhe = async () => {
            try {
                setLoadingDetalhe(true);
                setErrorDetalhe(null);
                const response = await axios.get(`${API_URL}/${grupo.Identificador}`);
                setProdutosDoGrupo(response.data);
            } catch (err) {
                console.error("Erro ao buscar detalhes do grupo:", err);
                const errorMessage = err.response?.data?.message || err.message;
                setErrorDetalhe(`Falha ao carregar os produtos do grupo ${grupo.Nome}. Detalhe: ${errorMessage}`);
                setProdutosDoGrupo([]); 
            } finally {
                setLoadingDetalhe(false);
            }
        };

        if (grupo.Identificador) {
            fetchDetalhe();
        } else {
            setLoadingDetalhe(false);
            setErrorDetalhe("Identificador do grupo ausente.");
        }
    }, [grupo.Identificador, grupo.Nome]);

    return (
        <div className="p-4 sm:p-8 bg-white rounded-xl shadow-2xl min-h-[500px]">
            <header className="flex items-center justify-between mb-6 border-b pb-4">
                <button
                    onClick={onBack}
                    className="flex items-center font-semibold hover:text-gray-900 transition duration-150"
                    style={{ color: BLACK_PRIMARY }}
                >
                    <ArrowLeft className="w-5 h-5 mr-2" />
                    Voltar para o Catálogo
                </button>
                <h1 className="text-3xl font-extrabold text-gray-900">{grupo.Nome}</h1>
                <X className="w-6 h-6 text-transparent" />
            </header>

            <h2 className="text-xl font-bold text-gray-700 mb-4">Produtos Disponíveis:</h2>

            {loadingDetalhe ? (
                <div className="flex items-center justify-center p-10">
                    <Loader2 className="w-6 h-6 mr-2 animate-spin" style={{ color: GOLD_PRIMARY }} />
                    <p>Buscando Produtos Reais do Bimer...</p>
                </div>
            ) : errorDetalhe ? (
                <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded" role="alert">
                    <p className="font-bold">Erro ao Listar Produtos</p>
                    <p>{errorDetalhe}</p>
                </div>
            ) : produtosDoGrupo.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {produtosDoGrupo.map(produto => (
                        <div key={produto.id} className="bg-gray-50 p-4 rounded-lg shadow-md hover:shadow-lg transition duration-300 flex flex-col justify-between" 
                             style={{ borderLeft: `4px solid ${GOLD_PRIMARY}` }}>
                            <div>
                                <h3 className="text-lg font-bold text-gray-900">{produto.nome}</h3>
                                <p className="text-sm text-gray-600 my-1">{produto.descricao}</p>
                                <p className="text-xs text-gray-400">Cód: {produto.codigo}</p>
                            </div>
                            <div className="flex justify-between items-center mt-3 pt-3 border-t border-gray-200">
                                <span className="text-xl font-extrabold text-green-700">{produto.preco}</span>
                                <button 
                                    onClick={() => handleAddToCart(produto)}
                                    style={{ backgroundColor: BLACK_PRIMARY, color: GOLD_PRIMARY }}
                                    className="px-4 py-2 rounded-lg font-semibold transition duration-150 shadow-md flex items-center hover:opacity-80"
                                >
                                    Adicionar <ShoppingCart className="w-4 h-4 ml-2" />
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <div className="text-center p-10 text-gray-500">
                    Nenhum produto encontrado para este grupo.
                </div>
            )}
        </div>
    );
};


// --- Componente: Catálogo (Lista de Grupos) ---
const CatalogoPage = ({ produtos, filteredProdutos, searchTerm, handleSearchChange, setSelectedGrupo }) => (
    <div className="p-4 sm:p-8">
        <header className="max-w-7xl mx-auto mb-10 text-center">
            <h1 className="text-4xl font-black text-gray-900 tracking-tight">
                Catálogo de Grupos de Produtos
            </h1>
            <p className="mt-3 text-lg text-gray-600">
                Clique em um grupo para ver os produtos.
            </p>
        </header>

        <div className="max-w-4xl mx-auto mb-8">
            <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                <input
                    type="text"
                    placeholder={`Pesquisar em ${produtos.length} grupos...`}
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg shadow-inner focus:ring-4 focus:ring-opacity-50 transition duration-150 text-base"
                    style={{ focusRingColor: GOLD_PRIMARY }}
                />
            </div>
        </div>

        <main className="max-w-7xl mx-auto">
            <p className="text-right text-sm text-gray-500 mb-4">
                {filteredProdutos.length} {filteredProdutos.length === 1 ? 'grupo encontrado' : 'grupos encontrados'}
            </p>

            {filteredProdutos.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {filteredProdutos.map((produto) => (
                        <ProdutoCard key={produto.Identificador} produto={produto} setSelectedGrupo={setSelectedGrupo} />
                    ))}
                </div>
            ) : (
                <div className="text-center p-12 bg-white rounded-xl shadow-lg">
                    <Package className="w-12 h-12 mx-auto mb-3" style={{ color: GOLD_PRIMARY }} />
                    <h3 className="text-xl font-semibold text-gray-900">Nenhum Grupo Encontrado</h3>
                    <p className="mt-2 text-gray-500">
                        A pesquisa por "{searchTerm}" não retornou resultados.
                    </p>
                </div>
            )}
        </main>
    </div>
);

// Componente Card para cada grupo de produto
const ProdutoCard = ({ produto, setSelectedGrupo }) => (
    <div 
        className="bg-white p-6 rounded-xl shadow-lg transition duration-300 transform border-t-4 hover:shadow-2xl hover:scale-[1.02] cursor-pointer"
        onClick={() => setSelectedGrupo(produto)}
        style={{ borderColor: GOLD_PRIMARY }}
    >
        <div className="flex justify-between items-start mb-4">
            <h2 className="text-2xl font-extrabold text-gray-900 leading-tight">
                {produto.Nome}
            </h2>
            <div className="text-xs font-semibold px-3 py-1 rounded-full flex items-center" 
                 style={{ backgroundColor: BLACK_PRIMARY, color: GOLD_PRIMARY }}>
                <Tag className="w-3 h-3 mr-1" />
                {produto.categoria}
            </div>
        </div>

        <p className="text-sm text-gray-500 mb-4">{produto.descricao}</p>
        
        <div className="border-t border-gray-100 pt-4 flex justify-between items-center">
            <div className="flex items-center text-green-700 font-bold">
                <DollarSign className="w-5 h-5 mr-1" />
                <span className="text-lg">{produto.precoVenda}</span>
            </div>
            <div className="text-gray-600 text-sm flex items-center">
                <Package className="w-4 h-4 mr-1" />
                Código: {produto.Codigo}
            </div>
        </div>
    </div>
);


// --- Componente: Histórico de Compras ---
const HistoricoPage = ({ currentUserType }) => (
    <div className="p-4 sm:p-8 max-w-7xl mx-auto">
        <header className="mb-8 border-b pb-4">
            <h1 className="text-4xl font-black text-gray-900 flex items-center">
                <History className="w-8 h-8 mr-3" style={{ color: GOLD_PRIMARY }} />
                Histórico de Compras do Cliente ({currentUserType})
            </h1>
            <p className="mt-2 text-gray-600">Revisite suas compras anteriores com facilidade.</p>
        </header>

        <div className="space-y-4">
            {MOCK_HISTORICO.map(compra => (
                <div key={compra.id} className="bg-white p-5 rounded-xl shadow-md border-l-4 flex justify-between items-center" 
                     style={{ borderColor: GOLD_PRIMARY }}>
                    <div>
                        <p className="text-lg font-bold text-gray-800">Pedido #{compra.id}</p>
                        <p className="text-sm text-gray-500">Data: {compra.data} | {compra.itens} itens</p>
                    </div>
                    <div className="text-right">
                        <p className="text-xl font-extrabold text-green-700">{compra.total}</p>
                        <span className={`px-3 py-1 text-xs font-semibold rounded-full mt-1 inline-block ${
                            compra.status === 'Entregue' ? 'bg-green-100 text-green-800' :
                            compra.status === 'Enviado' ? 'bg-blue-100 text-blue-800' :
                            'bg-red-100 text-red-800'
                        }`}>
                            {compra.status}
                        </span>
                    </div>
                </div>
            ))}
        </div>
    </div>
);


// --- Componente: Contato e Informações ---
const ContatoPage = () => (
    <div className="p-4 sm:p-8 max-w-4xl mx-auto">
        <header className="mb-8 border-b pb-4 text-center">
            <h1 className="text-4xl font-black text-gray-900">
                Entre em Contato | Ouro Branco
            </h1>
            <p className="mt-2 text-lg text-gray-600">Estamos prontos para te atender.</p>
        </header>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-center">
            <InfoCard icon={<Phone className="w-8 h-8 mx-auto mb-3" style={{ color: GOLD_PRIMARY }} />} title="Telefone" value={CONTACT_INFO.telefone} />
            <InfoCard icon={<Mail className="w-8 h-8 mx-auto mb-3" style={{ color: GOLD_PRIMARY }} />} title="E-mail" value={CONTACT_INFO.email} />
            <InfoCard icon={<Truck className="w-8 h-8 mx-auto mb-3" style={{ color: GOLD_PRIMARY }} />} title="Frete Mínimo (Isenção)" value={CONTACT_INFO.freteMinimo} />
        </div>
        
        <h2 className="text-2xl font-bold text-gray-800 mt-10 mb-4 border-b pb-2">Acesso para Funcionários (Ouro Branco)</h2>
        <div className="space-y-2">
            {EMPLOYEE_ROLES.map(role => (
                <div key={role} className="bg-gray-100 p-3 rounded-lg flex items-center justify-between">
                    <span className="font-medium text-gray-700">{role}</span>
                    <Lock className="w-5 h-5 text-red-500" />
                </div>
            ))}
            <p className="text-sm text-gray-500 pt-2">Entre em contato via telefone ou e-mail para obter o acesso restrito ao seu setor.</p>
        </div>
    </div>
);

const InfoCard = ({ icon, title, value }) => (
    <div className="bg-white p-6 rounded-xl shadow-lg border-t-4" style={{ borderColor: GOLD_PRIMARY }}>
        {icon}
        <h3 className="text-xl font-bold text-gray-900 mb-1">{title}</h3>
        <p className="font-semibold" style={{ color: BLACK_PRIMARY }}>{value}</p>
    </div>
);


// --- Componente: Acesso Restrito/Login (Simulado) ---
const AcessoRestritoPage = ({ currentUserType, setCurrentUserType }) => {
    const [selectedType, setSelectedType] = useState(currentUserType);

    const handleLogin = () => {
        setCurrentUserType(selectedType);
    };

    return (
        <div className="p-4 sm:p-8 max-w-md mx-auto">
            <header className="mb-8 text-center">
                <h1 className="text-3xl font-black text-gray-900">
                    <User className="w-6 h-6 inline mr-2" style={{ color: GOLD_PRIMARY }} />
                    Identificação de Cliente
                </h1>
                <p className="mt-2 text-gray-600">Selecione seu segmento para uma experiência personalizada.</p>
            </header>

            <div className="bg-white p-6 rounded-xl shadow-2xl">
                <label htmlFor="customerType" className="block text-sm font-medium text-gray-700 mb-2">
                    Você se identifica como:
                </label>
                <select
                    id="customerType"
                    value={selectedType}
                    onChange={(e) => setSelectedType(e.target.value)}
                    className="w-full py-3 px-4 border border-gray-300 rounded-lg shadow-sm focus:ring-4 focus:ring-opacity-50 transition duration-150"
                    style={{ focusRingColor: GOLD_PRIMARY }}
                >
                    {CUSTOMER_TYPES.map(type => (
                        <option key={type} value={type}>{type}</option>
                    ))}
                </select>

                <button 
                    onClick={handleLogin} 
                    className="w-full mt-6 py-3 text-white rounded-lg font-bold transition duration-150 shadow-md hover:opacity-90"
                    style={{ backgroundColor: BLACK_PRIMARY, color: GOLD_PRIMARY }}
                >
                    Confirmar Identidade
                </button>

                {currentUserType && (
                    <div className="mt-4 p-3 rounded-lg text-sm font-medium" style={{ backgroundColor: '#FDFDD0', color: BLACK_PRIMARY, border: `1px solid ${GOLD_PRIMARY}` }}>
                        Logado como: <span className="font-bold">{currentUserType}</span>
                    </div>
                )}
            </div>
        </div>
    );
};


// --- COMPONENTE PRINCIPAL APP ---
const App = () => {
    const [currentPage, setCurrentPage] = useState('catalogo'); 
    const [produtos, setProdutos] = useState([]); 
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedGrupo, setSelectedGrupo] = useState(null); 

    const [isCartOpen, setIsCartOpen] = useState(false);
    const [cart, setCart] = useState([]); 
    
    const [currentUserType, setCurrentUserType] = useState(CUSTOMER_TYPES[0]); 

    // Efeito para carregar a lista inicial de GRUPOS do backend
    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                setLoading(true);
                setError(null);
                const response = await axios.get(API_URL);
                setProdutos(response.data);
            } catch (err) {
                console.error("Erro ao buscar grupos:", err);
                const errorMessage = err.response?.data?.message || err.message;
                setError(`Falha ao carregar dados. Detalhe: ${errorMessage}`);
                setProdutos([]); 
            } finally {
                setLoading(false);
            }
        };

        fetchProdutos();
    }, []); 

    // Lógica de Filtragem
    const filteredProdutos = useMemo(() => {
        if (!searchTerm) {
            return produtos;
        }
        const lowerCaseSearch = searchTerm.toLowerCase();
        return produtos.filter(produto => 
            produto.Nome.toLowerCase().includes(lowerCaseSearch) ||
            produto.Codigo.toLowerCase().includes(lowerCaseSearch) ||
            produto.categoria.toLowerCase().includes(lowerCaseSearch)
        );
    }, [produtos, searchTerm]);

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    // --- Carrinho de Compras Lógica ---
    const toggleCart = useCallback(() => {
        setIsCartOpen(prev => !prev);
    }, []);

    const handleAddToCart = useCallback((produto) => {
        const itemIndex = cart.findIndex(item => item.id === produto.id);

        if (itemIndex > -1) {
            const newCart = [...cart];
            newCart[itemIndex].quantidade += 1;
            setCart(newCart);
        } else {
            setCart(prev => [...prev, {
                id: produto.id,
                nome: produto.nome,
                preco: produto.preco, 
                quantidade: 1,
            }]);
        }
        setIsCartOpen(true); 
    }, [cart]);

    const handleCheckout = () => {
        alert("Simulação de Finalização de Compra! Carrinho será esvaziado.");
        setCart([]);
        setIsCartOpen(false);
    };
    
    // Renderização Principal do Conteúdo
    const renderContent = () => {
        if (selectedGrupo) {
            return (
                <GrupoDetalhe 
                    grupo={selectedGrupo} 
                    onBack={() => setSelectedGrupo(null)} 
                    handleAddToCart={handleAddToCart}
                />
            );
        }

        switch (currentPage) {
            case 'catalogo':
                return (
                    <CatalogoPage
                        produtos={produtos}
                        filteredProdutos={filteredProdutos}
                        searchTerm={searchTerm}
                        handleSearchChange={handleSearchChange}
                        setSelectedGrupo={setSelectedGrupo}
                    />
                );
            case 'historico':
                return <HistoricoPage currentUserType={currentUserType} />;
            case 'contato':
                return <ContatoPage />;
            case 'acesso':
                return <AcessoRestritoPage 
                            currentUserType={currentUserType} 
                            setCurrentUserType={setCurrentUserType}
                        />;
            default:
                return <CatalogoPage 
                            produtos={produtos} 
                            filteredProdutos={filteredProdutos} 
                            searchTerm={searchTerm} 
                            handleSearchChange={handleSearchChange} 
                            setSelectedGrupo={setSelectedGrupo}
                        />;
        }
    };


    // Renderização de Estados (Loading/Error/Success)
    if (loading && produtos.length === 0 && !error) {
        return (
            <div className="flex items-center justify-center min-h-screen bg-gray-50">
                <Loader2 className="w-8 h-8 mr-2 animate-spin" style={{ color: GOLD_PRIMARY }} />
                <p className="text-xl font-medium text-gray-700">Carregando catálogo de grupos...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="p-8 min-h-screen bg-red-50 text-red-800">
                <h1 className="text-2xl font-bold mb-4">Erro Crítico de API</h1>
                <p className="whitespace-pre-wrap">{error}</p>
                <p className="mt-4 font-semibold">Verifique seu console Node.js (backend) para mais detalhes.</p>
            </div>
        );
    }


    return (
        <div className="min-h-screen bg-gray-50 flex flex-col">
            {/* Cabeçalho Fixo */}
            <header className="sticky top-0 z-30 shadow-lg" style={{ backgroundColor: BLACK_PRIMARY }}>
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center py-4">
                        {/* Logo e Nome da Empresa */}
                        <div className="flex items-center space-x-2">
                            <Truck className="w-7 h-7" style={{ color: GOLD_PRIMARY }} />
                            <h1 className="text-2xl font-black text-white">OURO BRANCO</h1>
                            <span className="ml-4 text-xs font-semibold px-3 py-1 rounded-full" style={{ backgroundColor: GOLD_PRIMARY, color: BLACK_PRIMARY }}>
                                Cliente: {currentUserType}
                            </span>
                        </div>
                        
                        {/* Menu de Navegação */}
                        <nav className="hidden md:flex space-x-6 font-medium text-white">
                            <NavItem name="Catálogo" page="catalogo" currentPage={currentPage} setCurrentPage={setCurrentPage} icon={Home} />
                            <NavItem name="Histórico" page="historico" currentPage={currentPage} setCurrentPage={setCurrentPage} icon={History} />
                            <NavItem name="Contato" page="contato" currentPage={currentPage} setCurrentPage={setCurrentPage} icon={Phone} />
                            <NavItem name="Acesso" page="acesso" currentPage={currentPage} setCurrentPage={setCurrentPage} icon={User} />
                        </nav>

                        {/* Botão do Carrinho */}
                        <div className="flex items-center space-x-4">
                            <button
                                onClick={toggleCart}
                                className="relative p-2 rounded-full shadow-md hover:opacity-90 transition duration-150"
                                style={{ backgroundColor: GOLD_PRIMARY, color: BLACK_PRIMARY }}
                            >
                                <ShoppingCart className="w-6 h-6" />
                                {cart.length > 0 && (
                                    <span className="absolute top-0 right-0 inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none text-red-100 transform translate-x-1/2 -translate-y-1/2 bg-red-600 rounded-full">
                                        {cart.length}
                                    </span>
                                )}
                            </button>
                            <button className="p-2 md:hidden text-white hover:text-gray-300 rounded-full">
                                <Menu className="w-6 h-6" />
                            </button>
                        </div>
                    </div>
                </div>
            </header>

            {/* Conteúdo Principal */}
            <main className="flex-grow max-w-7xl mx-auto w-full">
                {renderContent()}
            </main>

            {/* Rodapé (Simples) */}
            <footer className="mt-10 p-4 text-center text-gray-600" style={{ backgroundColor: '#EEEEEE' }}>
                &copy; {new Date().getFullYear()} Ouro Branco - Todos os direitos reservados.
            </footer>

            {/* Drawer do Carrinho */}
            <CartDrawer
                cart={cart}
                isCartOpen={isCartOpen}
                toggleCart={toggleCart}
                handleCheckout={handleCheckout}
            />
        </div>
    );
};

// Componente auxiliar para Nav Item
const NavItem = ({ name, page, currentPage, setCurrentPage, icon: Icon }) => {
    const isActive = currentPage === page;
    const classes = isActive 
        ? ' border-b-2 pb-1 font-extrabold'
        : 'hover:text-gray-300 transition duration-150';

    return (
        <button 
            onClick={() => setCurrentPage(page)} 
            className={`flex items-center ${classes}`}
            style={{ color: isActive ? GOLD_PRIMARY : WHITE_PRIMARY, borderColor: GOLD_PRIMARY }}
        >
            <Icon className="w-5 h-5 mr-1" />
            {name}
        </button>
    );
};

export default App;
