import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';

// --- Contextos ---
import { AuthProvider, useAuth } from './context/AuthContext';
import { CartProvider } from './context/CartContext';

// --- Layout Components ---
import Header from './components/Header';
import Footer from './components/Footer';
import CartMenu from './components/CartMenu'; // Opcional: Se quiser usá-lo como modal/sidebar

// --- Pages ---
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import CatalogPage from './pages/CatalogPage';
import ProductDetailPage from './pages/ProductDetailPage';
import CheckoutPage from './pages/CheckoutPage';

// --- Componente Protetor de Rotas ---
const ProtectedRoute = ({ element: Element, ...rest }) => {
  const { isAuthenticated } = useAuth();
  // Se não estiver autenticado, redireciona para a página de login
  return isAuthenticated ? <Element {...rest} /> : <Navigate to="/acesso" replace />;
};

// --- Componente de Layout Central ---
// Este componente define a estrutura de todas as páginas (Header, Footer, Conteúdo)
const Layout = ({ children }) => {
  return (
    <div style={styles.appContainer}>
      <Header />
      
      <main style={styles.mainContent}>
        {children}
      </main>
      
      {/* O CartMenu seria tipicamente renderizado aqui, mas controlado por um estado global */}
      {/* Por enquanto, vamos deixá-lo renderizado (visível por padrão no seu código) */}
      {/* <CartMenu /> */}
      
      <Footer />
    </div>
  );
};

// --- Componente Principal ---
const App = () => {
  return (
    // O BrowserRouter deve envolver toda a aplicação
    <BrowserRouter>
      {/* Os provedores de contexto devem envolver o restante da aplicação para que todos tenham acesso aos dados */}
      <AuthProvider>
        <CartProvider>
          {/* O Layout é renderizado dentro do Router/Providers */}
          <Layout>
            <Routes>
              {/* Rotas Públicas */}
              <Route path="/" element={<HomePage />} />
              <Route path="/acesso" element={<LoginPage />} />
              <Route path="/catalogo" element={<CatalogPage />} />
              {/* Rota com parâmetro para os detalhes do produto */}
              <Route path="/catalogo/:productId" element={<ProductDetailPage />} />
              
              {/* Rota Protegida */}
              <Route 
                path="/finalizar-compra" 
                element={<ProtectedRoute element={CheckoutPage} />} 
              />
              
              {/* Rota de Confirmação (após o checkout) - Opcional */}
              <Route path="/pedido-confirmado" element={
                  <div style={{ padding: '50px', textAlign: 'center' }}>
                      <h1>✅ Pedido Recebido!</h1>
                      <p>Seu pedido foi processado com sucesso. Obrigado!</p>
                  </div>
              } />

              {/* Rota 404 - Não Encontrado */}
              <Route path="*" element={
                <div style={{ padding: '50px', textAlign: 'center' }}>
                    <h1>404 | Página Não Encontrada</h1>
                    <p>O endereço solicitado não existe.</p>
                </div>
              } />
            </Routes>
          </Layout>
        </CartProvider>
      </AuthProvider>
    </BrowserRouter>
  );
};

// Estilos de Layout
const styles = {
  appContainer: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
  },
  mainContent: {
    flex: 1, // Faz o conteúdo principal ocupar todo o espaço restante
  },
};


export default App;
