// --- Componente Principal (App) ---
const App = () => {
    // Controla qual página está visível: 'home' ou 'catalogo'
    const [currentPage, setCurrentPage] = useState('home'); 

    // Define qual componente renderizar
    const renderPage = () => {
        switch (currentPage) {
            case 'catalogo':
                return <CatalogoPage setPage={setCurrentPage} />;
            case 'home':
            default:
                return <HomePage setPage={setCurrentPage} />;
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 font-inter">
            {/* Header Fixo e Navegação */}
            <header className="bg-black shadow-lg sticky top-0 z-20">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">
                    <h1 className="text-3xl font-extrabold tracking-tight text-amber-500">
                        Portal Ouro Branco
                    </h1>
                    <nav className="flex space-x-4">
                        <button 
                            onClick={() => setCurrentPage('home')}
                            className={`flex items-center px-4 py-2 rounded-full font-medium transition duration-200 ${
                                currentPage === 'home' ? 'bg-amber-500 text-black shadow-md' : 'text-gray-300 hover:text-amber-500 hover:bg-gray-800'
                            }`}
                        >
                            <Home className="w-5 h-5 mr-2" /> Início
                        </button>
                        <button 
                            onClick={() => setCurrentPage('catalogo')}
                            className={`flex items-center px-4 py-2 rounded-full font-medium transition duration-200 ${
                                currentPage === 'catalogo' ? 'bg-amber-500 text-black shadow-md' : 'text-gray-300 hover:text-amber-500 hover:bg-gray-800'
                            }`}
                        >
                            <List className="w-5 h-5 mr-2" /> Catálogo
                        </button>
                    </nav>
                </div>
            </header>

            <main>
                {renderPage()}
            </main>
        </div>
    );
};

export default App;
