import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, Loader, Zap, Tag, Package } from 'lucide-react'; // Ícones modernos

// --- Componente de Cartão de Produto ---
const ProdutoCard = ({ produto }) => {
    return (
        <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition duration-300 transform hover:-translate-y-1 border border-gray-100">
            <div className="flex items-center justify-between mb-3">
                <h3 className="text-xl font-extrabold text-gray-800 truncate">{produto.nome}</h3>
                <div className="text-sm font-semibold text-white bg-amber-500 px-3 py-1 rounded-full shadow-md">
                    {produto.marca}
                </div>
            </div>
            
            <p className="text-3xl font-bold text-green-600 mb-4">
                R$ {produto.preco_base.toFixed(2).replace('.', ',')}
            </p>

            <div className="space-y-2 text-sm text-gray-600">
                <div className="flex items-center">
                    <Zap className="w-4 h-4 text-amber-500 mr-2 flex-shrink-0" />
                    <span>Código: **{produto.codigo}**</span>
                </div>
                <div className="flex items-center">
                    <Package className="w-4 h-4 text-sky-500 mr-2 flex-shrink-0" />
                    <span>Estoque: **{produto.estoque} unidades**</span>
                </div>
            </div>
            
            {produto.estoque === 0 && (
                <p className="mt-4 p-2 bg-red-100 text-red-700 text-sm font-medium rounded-lg flex items-center">
                    <Loader className="w-4 h-4 mr-2 animate-spin-slow" />
                    Produto Indisponível (Sem Estoque)
                </p>
            )}
        </div>
    );
};

// --- Componente Principal da Aplicação (App) ---
const App = () => {
    const [produtos, setProdutos] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                // A URL da API do Node.js é http://localhost:3000/api/produtos
                const response = await axios.get('http://localhost:3000/api/produtos');
                setProdutos(response.data);
                setLoading(false);
            } catch (err) {
                console.error("Erro ao conectar com o Backend:", err);
                // Como o backend está configurado para retornar dados simulados em caso de falha,
                // você pode receber um erro aqui se o backend não estiver LIGADO (npm start no backend).
                setError("Falha ao carregar os dados. Verifique se o backend (Node.js) está rodando na porta 3000.");
                setLoading(false);
            }
        };

        fetchProdutos();
    }, []);

    // Filtra os produtos com base no termo de busca
    const produtosFiltrados = produtos.filter(produto =>
        produto.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
        produto.codigo.toLowerCase().includes(searchTerm.toLowerCase()) ||
        produto.marca.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Renderização de Estados
    if (loading) {
        return (
            <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 p-4">
                <Loader className="w-12 h-12 text-amber-500 animate-spin-slow" />
                <p className="mt-4 text-xl font-semibold text-gray-700">Carregando Catálogo...</p>
                <p className="text-sm text-gray-500 mt-1">Buscando dados na API da Bimer.</p>
            </div>
        );
    }

    if (error && produtos.length === 0) {
         // Se houver erro E não houver produtos, mostra o aviso.
        return (
            <div className="min-h-screen flex flex-col items-center justify-center bg-red-50 p-4">
                 <p className="text-lg text-red-700 p-6 bg-white rounded-xl shadow-lg border-2 border-red-300 text-center">
                    {error}
                </p>
                <p className="mt-4 text-base text-gray-600">
                    Se o backend estiver rodando, o erro pode ser na **conexão real com a Bimer**.
                </p>
            </div>
        );
    }

    // Renderização Principal do Catálogo
    return (
        <div className="min-h-screen bg-gray-100 font-inter">
            {/* Header Laranja e Preto */}
            <header className="bg-black shadow-lg sticky top-0 z-10">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">
                    <h1 className="text-3xl font-extrabold tracking-tight text-amber-500">
                        Catálogo Ouro Branco <span className="text-gray-300 text-sm font-medium">| MVP</span>
                    </h1>
                    <Tag className="w-8 h-8 text-amber-500" />
                </div>
            </header>

            <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                {/* Barra de Pesquisa */}
                <div className="mb-8 p-5 bg-white rounded-2xl shadow-xl sticky top-20 z-10 border border-amber-300">
                    <div className="relative">
                        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                        <input
                            type="text"
                            placeholder="Buscar por nome, código ou marca..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="w-full py-3 pl-10 pr-4 border-2 border-gray-200 rounded-xl focus:ring-amber-500 focus:border-amber-500 transition duration-150 text-gray-700"
                        />
                    </div>
                    <p className="mt-3 text-sm text-gray-500 font-medium">
                        {produtosFiltrados.length} produtos encontrados (total: {produtos.length})
                    </p>
                </div>
                
                {/* Grid de Produtos */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {produtosFiltrados.length > 0 ? (
                        produtosFiltrados.map(produto => (
                            <ProdutoCard key={produto.id} produto={produto} />
                        ))
                    ) : (
                        <div className="col-span-full text-center py-10 bg-white rounded-xl shadow-lg">
                            <h2 className="text-2xl font-bold text-gray-700">Nenhum Produto Encontrado</h2>
                            <p className="text-gray-500 mt-2">Tente refinar sua busca ou limpar o campo acima.</p>
                        </div>
                    )}
                </div>
            </main>
        </div>
    );
};

export default App;
