import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, Loader, Zap, Tag, Package, Home, List, ArrowRight, User, KeyRound, Phone, Mail, UserPlus } from 'lucide-react';

// --- Componente: Cartão de Produto (Inalterado) ---
const ProdutoCard = ({ produto }) => {
    return (
        <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-2xl transition duration-300 transform hover:-translate-y-1 border border-gray-100">
            <div className="flex items-center justify-between mb-3">
                <h3 className="text-xl font-extrabold text-gray-800 truncate">{produto.nome}</h3>
                <div className="text-sm font-semibold text-white bg-amber-500 px-3 py-1 rounded-full shadow-md">
                    {produto.marca}
                </div>
            </div>
            
            <p className="text-3xl font-bold text-green-600 mb-4">
                R$ {produto.preco_base.toFixed(2).replace('.', ',')}
            </p>

            <div className="space-y-2 text-sm text-gray-600">
                <div className="flex items-center">
                    <Zap className="w-4 h-4 text-amber-500 mr-2 flex-shrink-0" />
                    <span>Código: **{produto.codigo}**</span>
                </div>
                <div className="flex items-center">
                    <Package className="w-4 h-4 text-sky-500 mr-2 flex-shrink-0" />
                    <span>Estoque: **{produto.estoque} unidades**</span>
                </div>
            </div>
            
            {produto.estoque === 0 && (
                <p className="mt-4 p-2 bg-red-100 text-red-700 text-sm font-medium rounded-lg flex items-center">
                    <Loader className="w-4 h-4 mr-2 animate-spin-slow" />
                    Produto Indisponível (Sem Estoque)
                </p>
            )}
        </div>
    );
};

// --- Componente: Página do Catálogo ---
const CatalogoPage = ({ setPage }) => {
    const [produtos, setProdutos] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                // Tenta chamar a API do Node.js
                const response = await axios.get('http://localhost:3000/api/produtos');
                setProdutos(response.data);
                setLoading(false);
            } catch (err) {
                console.error("Erro ao conectar com o Backend:", err);
                setError("Falha ao carregar os dados. Verifique se o backend (Node.js) está rodando na porta 3000. (Usando dados simulados se disponíveis)");
                setLoading(false);
            }
        };

        fetchProdutos();
    }, []);

    const produtosFiltrados = produtos.filter(produto =>
        produto.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
        produto.codigo.toLowerCase().includes(searchTerm.toLowerCase()) ||
        produto.marca.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (loading) {
        return (
            <div className="flex flex-col items-center justify-center bg-gray-50 p-4 min-h-[60vh]">
                <Loader className="w-12 h-12 text-amber-500 animate-spin" />
                <p className="mt-4 text-xl font-semibold text-gray-700">Carregando Catálogo...</p>
            </div>
        );
    }

    if (error && produtos.length === 0) {
        return (
            <div className="flex flex-col items-center justify-center bg-red-50 p-4 min-h-[60vh]">
                 <p className="text-lg text-red-700 p-6 bg-white rounded-xl shadow-lg border-2 border-red-300 text-center">
                    {error}
                </p>
            </div>
        );
    }

    return (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
            <div className="mb-8 p-5 bg-white rounded-2xl shadow-xl sticky top-[80px] z-10 border border-amber-300">
                <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                    <input
                        type="text"
                        placeholder="Buscar por nome, código ou marca..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="w-full py-3 pl-10 pr-4 border-2 border-gray-200 rounded-xl focus:ring-amber-500 focus:border-amber-500 transition duration-150 text-gray-700"
                    />
                </div>
                <p className="mt-3 text-sm text-gray-500 font-medium">
                    {produtosFiltrados.length} produtos encontrados (total: {produtos.length})
                </p>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {produtosFiltrados.length > 0 ? (
                    produtosFiltrados.map(produto => (
                        <ProdutoCard key={produto.id} produto={produto} />
                    ))
                ) : (
                    <div className="col-span-full text-center py-10 bg-white rounded-xl shadow-lg">
                        <h2 className="text-2xl font-bold text-gray-700">Nenhum Produto Encontrado</h2>
                        <p className="text-gray-500 mt-2">Tente refinar sua busca ou limpar o campo acima.</p>
                    </div>
                )}
            </div>
        </div>
    );
};

// --- Componente: Página Inicial (Home) ---
const HomePage = ({ setPage }) => {
    return (
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-20 text-center">
            <div className="bg-white p-10 md:p-16 rounded-3xl shadow-2xl border-4 border-amber-500/50 transform transition duration-500 hover:scale-[1.01]">
                <Home className="w-16 h-16 text-amber-500 mx-auto mb-6" />
                <h1 className="text-5xl font-extrabold text-gray-900 mb-4">
                    Bem-vindo ao Portal Ouro Branco
                </h1>
                <p className="text-xl text-gray-600 mb-10 max-w-2xl mx-auto">
                    Acesse o catálogo em tempo real para consultar preços, códigos e disponibilidade de estoque, ou cadastre-se como nosso novo revendedor parceiro!
                </p>
                
                <div className="flex flex-col sm:flex-row justify-center space-y-4 sm:space-y-0 sm:space-x-4">
                    <button
                        onClick={() => setPage('catalogo')}
                        className="flex items-center justify-center px-8 py-4 bg-amber-500 text-black text-xl font-bold rounded-full shadow-lg hover:bg-amber-600 transition duration-300 transform hover:shadow-xl hover:-translate-y-1"
                    >
                        <List className="w-6 h-6 mr-3" /> Catálogo de Produtos
                    </button>
                    <button
                        onClick={() => setPage('register')}
                        className="flex items-center justify-center px-8 py-4 bg-gray-800 text-white text-xl font-bold rounded-full shadow-lg hover:bg-gray-700 transition duration-300 transform hover:shadow-xl hover:-translate-y-1"
                    >
                        <UserPlus className="w-6 h-6 mr-3" /> Seja um Revendedor
                    </button>
                </div>

                <div className="mt-12 pt-8 border-t border-gray-100">
                    <h2 className="text-2xl font-semibold text-gray-700 mb-4">Recursos do Portal</h2>
                    <ul className="space-y-3 text-lg text-gray-600">
                        <li className="flex items-center justify-center">
                            <KeyRound className="w-5 h-5 text-green-500 mr-2" />
                            Acesso seguro e exclusivo (Login)
                        </li>
                        <li className="flex items-center justify-center">
                            <Zap className="w-5 h-5 text-green-500 mr-2" />
                            Visualização de estoque em tempo real (após integração com Bimer)
                        </li>
                    </ul>
                </div>
            </div>
        </div>
    );
};

// --- Componente: Página de Login ---
const LoginPage = ({ setPage }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Lógica de autenticação simulada:
        console.log('Tentativa de Login:', { email, password });
        alert("Função de Login ainda não implementada no Backend. Dados enviados para o console.");
        // Futuramente, aqui será feita uma chamada axios.post('/api/login', { email, password })
    };

    return (
        <div className="max-w-md mx-auto px-4 sm:px-6 lg:px-8 py-20">
            <div className="bg-white p-8 md:p-10 rounded-3xl shadow-2xl border-4 border-black/10">
                <KeyRound className="w-12 h-12 text-black mx-auto mb-6" />
                <h1 className="text-3xl font-extrabold text-gray-900 text-center mb-6">
                    Acesso de Revendedor
                </h1>
                
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Email</label>
                        <div className="mt-1 relative rounded-md shadow-sm">
                            <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                placeholder="seu@email.com"
                                required
                                className="block w-full pl-10 pr-3 py-3 border border-gray-300 rounded-lg placeholder-gray-400 focus:outline-none focus:ring-amber-500 focus:border-amber-500 text-gray-900"
                            />
                        </div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Senha</label>
                        <div className="mt-1 relative rounded-md shadow-sm">
                            <KeyRound className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                            <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                placeholder="Sua senha secreta"
                                required
                                className="block w-full pl-10 pr-3 py-3 border border-gray-300 rounded-lg placeholder-gray-400 focus:outline-none focus:ring-amber-500 focus:border-amber-500 text-gray-900"
                            />
                        </div>
                    </div>
                    
                    <button
                        type="submit"
                        className="w-full flex justify-center py-3 px-4 border border-transparent rounded-lg shadow-sm text-lg font-medium text-black bg-amber-500 hover:bg-amber-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-amber-500 transition duration-150"
                    >
                        Entrar
                    </button>
                </form>

                <div className="mt-6 text-center">
                    <button onClick={() => setPage('register')} className="text-sm font-medium text-gray-600 hover:text-amber-500 transition duration-150">
                        Não tem conta? **Cadastre-se como Revendedor**
                    </button>
                </div>
            </div>
        </div>
    );
};

// --- Componente: Cadastro de Revendedor (Nova) ---
const RevendedorRegistrationPage = ({ setPage }) => {
    const [formData, setFormData] = useState({
        nomeCompleto: '',
        telefone: '',
        email: '',
        password: '',
        confirmPassword: ''
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        if (formData.password !== formData.confirmPassword) {
            alert('A senha e a confirmação de senha não coincidem.');
            return;
        }

        // Lógica de submissão simulada:
        console.log('Novo Revendedor Cadastrado:', formData);
        alert("Solicitação de cadastro enviada com sucesso! Um representante entrará em contato. (Função de envio de dados para o Backend não implementada)");
        setPage('login'); // Redireciona para o login após o "sucesso"
    };

    const inputClasses = "block w-full pl-10 pr-3 py-3 border border-gray-300 rounded-lg placeholder-gray-400 focus:outline-none focus:ring-amber-500 focus:border-amber-500 text-gray-900";

    return (
        <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
            <div className="bg-white p-8 md:p-12 rounded-3xl shadow-2xl border-4 border-amber-500/10">
                <UserPlus className="w-12 h-12 text-amber-500 mx-auto mb-6" />
                <h1 className="text-3xl font-extrabold text-gray-900 text-center mb-2">
                    Seja um Novo Revendedor Ouro Branco
                </h1>
                <p className="text-center text-gray-600 mb-8">
                    Preencha o formulário abaixo para iniciar sua parceria e receber acesso exclusivo ao nosso portal.
                </p>
                
                <form onSubmit={handleSubmit} className="space-y-6">
                    {/* Dados Pessoais */}
                    <h2 className="text-xl font-semibold text-gray-700 border-b pb-2 mb-4">Informações de Contato</h2>
                    
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Nome Completo</label>
                        <div className="mt-1 relative rounded-md shadow-sm">
                            <User className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                            <input type="text" name="nomeCompleto" value={formData.nomeCompleto} onChange={handleChange} required className={inputClasses} placeholder="Seu nome completo" />
                        </div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Telefone</label>
                        <div className="mt-1 relative rounded-md shadow-sm">
                            <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                            <input type="tel" name="telefone" value={formData.telefone} onChange={handleChange} required className={inputClasses} placeholder="(XX) XXXXX-XXXX" />
                        </div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Email</label>
                        <div className="mt-1 relative rounded-md shadow-sm">
                            <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                            <input type="email" name="email" value={formData.email} onChange={handleChange} required className={inputClasses} placeholder="seu@email.com" />
                        </div>
                    </div>

                    {/* Dados de Login */}
                    <h2 className="text-xl font-semibold text-gray-700 border-b pb-2 pt-4 mb-4">Credenciais de Acesso</h2>

                    <div>
                        <label className="block text-sm font-medium text-gray-700">Senha (Para Login)</label>
                        <div className="mt-1 relative rounded-md shadow-sm">
                            <KeyRound className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                            <input type="password" name="password" value={formData.password} onChange={handleChange} required className={inputClasses} placeholder="Crie uma senha forte" />
                        </div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Confirmar Senha</label>
                        <div className="mt-1 relative rounded-md shadow-sm">
                            <KeyRound className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                            <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required className={inputClasses} placeholder="Confirme sua senha" />
                        </div>
                    </div>
                    
                    <button
                        type="submit"
                        className="w-full flex justify-center py-3 px-4 border border-transparent rounded-lg shadow-sm text-lg font-medium text-white bg-black hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-black transition duration-150"
                    >
                        Solicitar Cadastro
                    </button>
                </form>

                <div className="mt-6 text-center">
                    <button onClick={() => setPage('login')} className="text-sm font-medium text-gray-600 hover:text-amber-500 transition duration-150">
                        Já tem cadastro? **Voltar para o Login**
                    </button>
                </div>
            </div>
        </div>
    );
};


// --- Componente Principal (App) ---
const App = () => {
    // Controla qual página está visível: 'home', 'catalogo', 'login', 'register'
    const [currentPage, setCurrentPage] = useState('home'); 

    // Define qual componente renderizar
    const renderPage = () => {
        switch (currentPage) {
            case 'catalogo':
                return <CatalogoPage setPage={setCurrentPage} />;
            case 'login':
                return <LoginPage setPage={setCurrentPage} />;
            case 'register':
                return <RevendedorRegistrationPage setPage={setCurrentPage} />;
            case 'home':
            default:
                return <HomePage setPage={setCurrentPage} />;
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 font-inter">
            {/* Header Fixo e Navegação */}
            <header className="bg-black shadow-lg sticky top-0 z-20">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">
                    <h1 className="text-3xl font-extrabold tracking-tight text-amber-500">
                        Portal Ouro Branco
                    </h1>
                    <nav className="flex space-x-4">
                        <button 
                            onClick={() => setCurrentPage('home')}
                            className={`flex items-center px-4 py-2 rounded-full font-medium transition duration-200 ${
                                currentPage === 'home' ? 'bg-amber-500 text-black shadow-md' : 'text-gray-300 hover:text-amber-500 hover:bg-gray-800'
                            }`}
                        >
                            <Home className="w-5 h-5 mr-2" /> Início
                        </button>
                        <button 
                            onClick={() => setCurrentPage('catalogo')}
                            className={`flex items-center px-4 py-2 rounded-full font-medium transition duration-200 ${
                                currentPage === 'catalogo' ? 'bg-amber-500 text-black shadow-md' : 'text-gray-300 hover:text-amber-500 hover:bg-gray-800'
                            }`}
                        >
                            <List className="w-5 h-5 mr-2" /> Catálogo
                        </button>
                        <button 
                            onClick={() => setCurrentPage('login')}
                            className={`flex items-center px-4 py-2 rounded-full font-medium transition duration-200 ${
                                currentPage === 'login' || currentPage === 'register' ? 'bg-white text-black shadow-md' : 'text-gray-300 hover:text-white hover:bg-gray-800'
                            }`}
                        >
                            <User className="w-5 h-5 mr-2" /> Acesso
                        </button>
                    </nav>
                </div>
            </header>

            <main>
                {renderPage()}
            </main>
        </div>
    );
};

export default App;
