import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Search, Package, Lock, User, Home as HomeIcon, LogIn, ShoppingCart, Phone, Eye, EyeOff, BarChart3 } from 'lucide-react';

// URL base para o backend.
// ATENÇÃO: Ao invés de usar IP ou porta, usamos um caminho relativo '/api'.
// Seu servidor (45.70.32.79:8080) DEVE ser configurado para redirecionar
// requisições a '/api' para o seu Backend Proxy (bimer_proxy_server.js).
const API_BASE_URL = '/api'; 

// --- FUNÇÕES UTILITÁRIAS ---

// Função para fazer chamadas com backoff exponencial (para robustez)
const fetchWithBackoff = async (url, options = {}, retries = 3) => {
    let delay = 1000;
    for (let i = 0; i < retries; i++) {
        try {
            // Usando Axios para melhor manipulação de erros HTTP
            return await axios(url, options);
        } catch (error) {
            if (i === retries - 1) throw error;
            console.warn(`Erro na requisição, tentando novamente em ${delay / 1000}s...`, error);
            await new Promise(resolve => setTimeout(resolve, delay));
            delay *= 2;
        }
    }
};

// --- COMPONENTES DA PÁGINA ---

/**
 * Componente de Cabeçalho (Navbar)
 */
const Header = ({ currentPage, setCurrentPage, isLoggedIn, handleLogout }) => {
    // Retorna a cor do texto baseada na página ativa
    const getLinkClass = (page) =>
        `text-white text-sm font-medium p-2 rounded-lg transition duration-300 ${
            currentPage === page ? 'bg-yellow-600' : 'hover:bg-yellow-700'
        }`;

    return (
        <header className="bg-gray-900 shadow-md">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
                <div className="flex items-center space-x-4">
                    <span className="text-xl font-bold text-yellow-500 tracking-wider cursor-pointer" onClick={() => setCurrentPage('home')}>
                        Portal Ouro Branco
                    </span>
                    <nav className="hidden md:flex space-x-4">
                        <button onClick={() => setCurrentPage('home')} className={getLinkClass('home')}>
                            <HomeIcon className="inline w-4 h-4 mr-1" /> Início
                        </button>
                        <button onClick={() => setCurrentPage('search')} className={getLinkClass('search')}>
                            <Search className="inline w-4 h-4 mr-1" /> Pesquisa de Produtos
                        </button>
                        {/* BOTÃO PARA A INTEGRAÇÃO BIMER */}
                        {isLoggedIn && (
                             <button onClick={() => setCurrentPage('bimerIntegration')} className={getLinkClass('bimerIntegration')}>
                                <BarChart3 className="inline w-4 h-4 mr-1" /> Dados Bimer
                            </button>
                        )}
                    </nav>
                </div>

                <div className="flex items-center space-x-3">
                    <button onClick={() => setCurrentPage('cart')} className={getLinkClass('cart')}>
                        <ShoppingCart className="inline w-4 h-4 mr-1" /> Carrinho
                    </button>
                    
                    {isLoggedIn ? (
                        <>
                            <button onClick={() => setCurrentPage('profile')} className={getLinkClass('profile')}>
                                <User className="inline w-4 h-4 mr-1" /> Perfil
                            </button>
                            <button onClick={handleLogout} className="text-white text-sm font-medium p-2 rounded-lg bg-red-600 hover:bg-red-700 transition duration-300">
                                <LogIn className="inline w-4 h-4 mr-1 transform rotate-180" /> Sair
                            </button>
                        </>
                    ) : (
                        <button onClick={() => setCurrentPage('login')} className={getLinkClass('login')}>
                            <LogIn className="inline w-4 h-4 mr-1" /> Entrar
                        </button>
                    )}
                </div>
            </div>
        </header>
    );
};

/**
 * Componente de Rodapé
 */
const Footer = () => (
    <footer className="bg-gray-800 text-white p-6 mt-8 shadow-inner">
        <div className="max-w-7xl mx-auto text-center text-sm">
            <p>&copy; {new Date().getFullYear()} Portal Ouro Branco. Todos os direitos reservados.</p>
            <p className="mt-2 flex justify-center items-center space-x-2">
                <Phone className="w-4 h-4" /> <span>Contato: (XX) XXXX-XXXX</span>
            </p>
        </div>
    </footer>
);

/**
 * Componente de Notificação
 */
const useNotification = () => {
    const [notification, setNotification] = useState(null);

    const showNotification = useCallback((message, type = 'success', duration = 3000) => {
        setNotification({ message, type });
        const timer = setTimeout(() => {
            setNotification(null);
        }, duration);
        return () => clearTimeout(timer); // Função de limpeza
    }, []);

    return [notification, showNotification];
};

/**
 * Página de Login
 */
const LoginPage = ({ setCurrentPage, handleLogin, showNotification }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        // Simulação de login: Verifica se a senha é '123456'
        if (email === 'mari.xavier' && password === '123456') {
            handleLogin(true);
            setCurrentPage('home');
            showNotification('Login realizado com sucesso!', 'success');
        } else {
            showNotification('Credenciais inválidas. Tente novamente.', 'error');
        }
        setIsLoading(false);
    };

    return (
        <div className="flex justify-center items-center min-h-screen-minus-header bg-gray-100">
            <div className="w-full max-w-md bg-white p-8 rounded-xl shadow-2xl">
                <h2 className="text-3xl font-extrabold text-gray-900 text-center mb-6">
                    Acesso ao Portal
                </h2>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                            Usuário Bimer (mari.xavier)
                        </label>
                        <input
                            id="email"
                            name="email"
                            type="text"
                            required
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-yellow-500 focus:border-yellow-500"
                            placeholder="mari.xavier"
                        />
                    </div>
                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-1">
                            Senha Bimer (123456)
                        </label>
                        <div className="relative">
                            <input
                                id="password"
                                name="password"
                                type={showPassword ? 'text' : 'password'}
                                required
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg shadow-sm focus:ring-yellow-500 focus:border-yellow-500 pr-10"
                                placeholder="123456"
                            />
                            <button
                                type="button"
                                onClick={() => setShowPassword(!showPassword)}
                                className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-500 hover:text-gray-700"
                                aria-label={showPassword ? 'Ocultar senha' : 'Mostrar senha'}
                            >
                                {showPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
                            </button>
                        </div>
                    </div>
                    <div>
                        <button
                            type="submit"
                            disabled={isLoading}
                            className={`w-full flex justify-center py-2 px-4 border border-transparent rounded-lg shadow-sm text-lg font-semibold text-gray-900 bg-yellow-500 hover:bg-yellow-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500 transition duration-150 ${isLoading ? 'opacity-50 cursor-not-allowed' : ''}`}
                        >
                            {isLoading ? 'Entrando...' : 'Entrar'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};


/**
 * Página Principal (Home)
 */
const HomePage = ({ setCurrentPage }) => (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="text-center">
            <h1 className="text-5xl font-extrabold text-gray-900 mb-4">
                Bem-vindo ao Portal Ouro Branco
            </h1>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
                Sua plataforma centralizada para gestão, pedidos e informações de produtos.
            </p>
        </div>

        <div className="mt-10 grid grid-cols-1 md:grid-cols-3 gap-8">
            <FeatureCard 
                icon={Search} 
                title="Pesquisa Avançada" 
                description="Encontre rapidamente os produtos que você precisa com filtros inteligentes e detalhes completos."
                onClick={() => setCurrentPage('search')}
                color="blue"
            />
            <FeatureCard 
                icon={Package} 
                title="Status de Pedidos" 
                description="Acompanhe o status em tempo real de todas as suas compras, do pedido à entrega."
                onClick={() => setCurrentPage('profile')}
                color="green"
            />
            <FeatureCard 
                icon={BarChart3} 
                title="Dados Gerenciais Bimer" 
                description="Acesse informações financeiras e de estoque integradas diretamente do seu sistema Bimer."
                onClick={() => setCurrentPage('bimerIntegration')}
                color="yellow"
            />
        </div>
    </div>
);

/**
 * Card de Funcionalidade
 */
const FeatureCard = ({ icon: Icon, title, description, onClick, color }) => {
    const colorClasses = {
        blue: 'bg-blue-500 hover:bg-blue-600',
        green: 'bg-green-500 hover:bg-green-600',
        yellow: 'bg-yellow-500 hover:bg-yellow-600',
    };

    return (
        <div 
            className="p-6 bg-white rounded-xl shadow-lg transform hover:scale-[1.02] transition duration-300 cursor-pointer border border-gray-200"
            onClick={onClick}
        >
            <div className={`p-3 inline-flex items-center justify-center h-12 w-12 rounded-full ${colorClasses[color]} text-white mb-4`}>
                <Icon className="w-6 h-6" />
            </div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">{title}</h3>
            <p className="text-gray-600">{description}</p>
        </div>
    );
};

// Placeholder de Páginas
const SearchPage = () => (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <h1 className="text-4xl font-extrabold text-gray-900 mb-6">Pesquisa de Produtos (Placeholder)</h1>
        <p className="text-gray-600">Componente de pesquisa de produtos a ser implementado. Aqui você listaria e filtraria os produtos.</p>
    </div>
);
const ProfilePage = () => (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <h1 className="text-4xl font-extrabold text-gray-900 mb-6">Meu Perfil (Placeholder)</h1>
