/**
 * NOVA PÁGINA: Integração de Dados Bimer
 */
const BimerIntegrationPage = ({ showNotification, isLoggedIn }) => {
    const [bimerData, setBimerData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchBimerData = useCallback(async () => {
        if (!isLoggedIn) {
            setError('Você precisa estar logado para acessar os dados do Bimer.');
            return;
        }

        setIsLoading(true);
        setError(null);
        setBimerData(null);

        try {
            // AQUI FAZEMOS A CHAMADA PARA O NOSSO ENDPOINT PROXY (EX: 45.70.32.79:8080/api/bimer-data-proxy)
            // O servidor DEVE reencaminhar esta chamada para o Node.js interno.
            const response = await fetchWithBackoff(`${API_BASE_URL}/bimer-data-proxy`);
            
            // Supondo que o proxy retorna os dados processados:
            setBimerData(response.data.data);
            showNotification('Dados do Bimer carregados com sucesso!', 'success');

        } catch (err) {
            const errorMessage = err.response?.data?.error || 'Erro ao comunicar com o servidor proxy. Verifique se o servidor Node.js (bimer_proxy_server.js) está rodando e se o Proxy Reverso do Nginx/Apache está configurado corretamente no 45.70.32.79:8080.';
            setError(errorMessage);
            showNotification(errorMessage, 'error');
            console.error("Erro completo ao buscar dados do Bimer:", err);
        } finally {
            setIsLoading(false);
        }
    }, [showNotification, isLoggedIn]);

    useEffect(() => {
        fetchBimerData();
    }, [fetchBimerData]);

    return (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
            <h1 className="text-4xl font-extrabold text-gray-900 mb-6">
                Dados Gerenciais Bimer
            </h1>
            <p className="text-gray-600 mb-8">
                Visualização de dados críticos de estoque e finanças extraídos da API Bimer via servidor proxy.
            </p>

            <button 
                onClick={fetchBimerData}
                disabled={isLoading}
                className="mb-8 px-6 py-2 bg-yellow-500 text-gray-900 font-semibold rounded-lg hover:bg-yellow-600 transition duration-150 disabled:opacity-50 disabled:cursor-not-allowed"
            >
                {isLoading ? 'Recarregando...' : 'Recarregar Dados'}
            </button>

            {isLoading && (
                <div className="flex justify-center items-center h-64">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-500"></div>
                    <p className="ml-4 text-lg text-gray-600">Carregando dados da API Bimer...</p>
                </div>
            )}

            {error && (
                <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-lg" role="alert">
                    <p className="font-bold">Erro de Comunicação do Proxy</p>
                    <p>{error}</p>
                    <p className="mt-2 text-sm">Verifique o log do seu servidor Node.js/Proxy (`bimer_proxy_server.js`) para detalhes da falha de conexão com `192.168.5.16:8085`.</p>
                </div>
            )}

            {bimerData && bimerData.length > 0 && (
                <div className="bg-white shadow-xl rounded-xl overflow-hidden">
                    <table className="min-w-full divide-y divide-gray-200">
                        <thead className="bg-gray-100">
                            <tr>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID Documento</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Descrição</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Valor</th>
                            </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                            {bimerData.map((item, index) => (
                                <tr key={index} className="hover:bg-yellow-50 transition duration-150">
                                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{item.id}</td>
                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{item.description}</td>
                                    <td className="px-6 py-4 whitespace-nowrap text-sm">
                                        <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                            item.status === 'APROVADO' ? 'bg-green-100 text-green-800' : 
                                            item.status === 'PENDENTE' ? 'bg-yellow-100 text-yellow-800' :
                                            'bg-red-100 text-red-800'
                                        }`}>
                                            {item.status}
                                        </span>
                                    </td>
                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">R$ {item.value.toFixed(2).replace('.', ',')}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}

            {bimerData && bimerData.length === 0 && !isLoading && !error && (
                 <div className="p-8 bg-gray-50 rounded-lg text-center">
                    <p className="text-lg text-gray-500">Nenhum dado do Bimer encontrado para exibição.</p>
                </div>
            )}
        </div>
    );
};


/**
 * Componente Principal da Aplicação
 */
export default function App() {
    const [currentPage, setCurrentPage] = useState('home');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [notification, showNotification] = useNotification();
    const [user, setUser] = useState(null); // Estado para armazenar dados do usuário, se necessário

    const handleLogin = (success) => {
        setIsLoggedIn(success);
        // Simulação de dados de usuário após login
        if (success) {
             setUser({ email: 'mari.xavier', name: 'Mari Xavier' });
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUser(null);
        setCurrentPage('home');
        showNotification('Você saiu do sistema.', 'success');
    };

    const renderPage = () => {
        switch (currentPage) {
            case 'login':
                return <LoginPage setCurrentPage={setCurrentPage} handleLogin={handleLogin} showNotification={showNotification} />;
            case 'search':
                return <SearchPage />;
            case 'profile':
                return <ProfilePage />;
            case 'cart':
                return <CartPage />;
            case 'bimerIntegration': // NOVA ROTA
                 return <BimerIntegrationPage showNotification={showNotification} isLoggedIn={isLoggedIn} />;
            default:
                return <HomePage setCurrentPage={setCurrentPage} />;
        }
    };

    return (
        <div className="relative min-h-screen flex flex-col font-sans">
            <style jsx="true">{`
                /* Classe de utilidade para dar um espaçamento entre o header e o footer */
                .min-h-screen-minus-header {
                    min-height: calc(100vh - 120px); /* Ajustado para compensar a altura do Header e do Footer */
                }
            `}</style>

            <Header 
                currentPage={currentPage} 
                setCurrentPage={setCurrentPage} 
                isLoggedIn={isLoggedIn} 
                handleLogout={handleLogout} 
            />
            
            <main className="flex-grow">
                {renderPage()}
            </main>

            <Footer />

            {/* Componente de Notificação Flutuante */}
            {notification && (
                <div
                    className={`fixed bottom-4 right-4 p-4 rounded-lg shadow-xl text-white font-semibold transition-opacity duration-300 ${
                        notification.type === 'success' ? 'bg-green-600' : 'bg-red-600'
                    }`}
                    style={{ zIndex: 100 }} // Garante que a notificação fique acima de tudo
                >
                    {notification.message}
                </div>
            )}
        </div>
    );
}
