// === COMPONENTE PRINCIPAL (App) ===
const App = () => {
    // Gerenciamento de visualização (simula o roteamento)
    const [view, setView] = useState('home'); 
    const [showSearchModal, setShowSearchModal] = useState(false);

    const handleViewChange = useCallback((newView) => {
        setView(newView);
    }, []);

    // Função para renderizar o conteúdo central
    const renderContent = () => {
        // Verifica se a view é um grupo de produto (Ex: 'group-005072')
        if (view.startsWith('group-')) {
            const groupId = view.split('-')[1];
            return <ProductGroupScreen onViewChange={handleViewChange} groupId={groupId} />;
        }

        switch (view) {
            case 'home':
                return <HomeScreen onViewChange={handleViewChange} />;
            case 'catalog':
                return <CatalogScreen onViewChange={handleViewChange} />;
            case 'access':
                return <AccessScreen onViewChange={handleViewChange} />;
            case 'history':
            case 'contact':
                return <div className="p-10 text-xl font-semibold text-gray-600">Página em Construção: {view.toUpperCase()}</div>
            default:
                return <HomeScreen onViewChange={handleViewChange} />;
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center font-sans">
            <Navbar 
                onViewChange={handleViewChange} 
                currentView={view} 
                onSearchClick={() => setShowSearchModal(true)} 
            />
            
            <main className="flex-grow flex items-center justify-center p-4 w-full">
                {renderContent()}
            </main>

            <footer className="w-full bg-gray-800 p-4 text-center text-gray-400 text-sm mt-auto">
                © 2025 Ouro Branco. Todos os direitos reservados.
            </footer>

            {/* Renderiza o Modal de Pesquisa se showSearchModal for true */}
            {showSearchModal && <SearchProductModal onClose={() => setShowSearchModal(false)} />}
        </div>
    );
};

export default App;
