import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Search, Package, Lock, User, Home as HomeIcon, LogIn, ShoppingCart, Phone, Eye, EyeOff, XCircle, CheckCircle } from 'lucide-react';

// URL base para o backend (ajuste se a porta mudar)
const API_BASE_URL = 'http://localhost:3000/api';

// Configuração para uso do Firebase (assumindo que o setup está completo)
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};

// Função auxiliar para atraso
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

// --- COMPONENTES DA PÁGINA ---

/**
 * Componente de Cabeçalho (Navbar)
 */
const Header = ({ currentPage, setCurrentPage, isLoggedIn, handleLogout }) => {
    // Retorna a cor do texto baseada na página ativa
    const getLinkClass = (page) =>
        `text-white text-sm font-medium p-2 rounded-lg transition duration-300 ${
            currentPage === page ? 'bg-yellow-600' : 'hover:bg-yellow-700'
        }`;

    return (
        <header className="bg-gray-900 shadow-md sticky top-0 z-10">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
                <div className="flex items-center space-x-4">
                    <span className="text-xl font-bold text-yellow-500 tracking-wider">
                        Portal Ouro Branco
                    </span>
                    <nav className="hidden md:flex space-x-4">
                        <button onClick={() => setCurrentPage('home')} className={getLinkClass('home')}>
                            <HomeIcon className="inline-block w-4 h-4 mr-1" /> Início
                        </button>
                        <button onClick={() => setCurrentPage('products')} className={getLinkClass('products')}>
                            <Package className="inline-block w-4 h-4 mr-1" /> Produtos BIMER
                        </button>
                    </nav>
                </div>
                
                <div className="flex items-center space-x-2">
                    {isLoggedIn ? (
                        <>
                            <button onClick={() => setCurrentPage('admin')} className={getLinkClass('admin')}>
                                <Lock className="inline-block w-4 h-4 mr-1" /> Admin
                            </button>
                            <button onClick={handleLogout} className="bg-red-600 hover:bg-red-700 text-white text-sm font-medium p-2 rounded-lg transition duration-300">
                                <LogOut className="inline-block w-4 h-4 mr-1" /> Sair
                            </button>
                        </>
                    ) : (
                        <button onClick={() => setCurrentPage('login')} className={getLinkClass('login')}>
                            <LogIn className="inline-block w-4 h-4 mr-1" /> Login
                        </button>
                    )}
                </div>
            </div>
            {/* Nav para mobile */}
            <nav className="md:hidden flex justify-around p-2 bg-gray-800 border-t border-gray-700">
                <button onClick={() => setCurrentPage('home')} className={getLinkClass('home')}>
                    <HomeIcon className="w-5 h-5" />
                </button>
                <button onClick={() => setCurrentPage('products')} className={getLinkClass('products')}>
                    <Package className="w-5 h-5" />
                </button>
                {isLoggedIn && (
                    <button onClick={() => setCurrentPage('admin')} className={getLinkClass('admin')}>
                        <Lock className="w-5 h-5" />
                    </button>
                )}
                <button onClick={() => setCurrentPage(isLoggedIn ? 'admin' : 'login')} className={getLinkClass(isLoggedIn ? 'admin' : 'login')}>
                    {isLoggedIn ? <User className="w-5 h-5" /> : <LogIn className="w-5 h-5" />}
                </button>
            </nav>
        </header>
    );
};

/**
 * Componente de Rodapé
 */
const Footer = () => (
    <footer className="bg-gray-900 border-t border-gray-800 text-white py-4 mt-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center text-sm">
            <p>&copy; {new Date().getFullYear()} Portal Ouro Branco. Todos os direitos reservados.</p>
            <p className="mt-1">
                <Phone className="inline-block w-3 h-3 mr-1" /> Contato: (XX) XXXX-XXXX | E-mail: contato@ourobranco.com
            </p>
        </div>
    </footer>
);

/**
 * Página Inicial
 */
const HomePage = ({ setCurrentPage }) => (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 text-gray-800">
        <div className="text-center mb-12">
            <h1 className="text-5xl font-extrabold text-gray-900 mb-4">Bem-vindo ao Portal Ouro Branco</h1>
            <p className="text-xl text-gray-600">Sua fonte de produtos de alta qualidade e soluções inovadoras.</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition duration-300 border-t-4 border-yellow-500">
                <Package className="w-10 h-10 text-yellow-600 mb-3" />
                <h2 className="text-2xl font-semibold mb-2 text-gray-800">Produtos BIMER em Destaque</h2>
                <p className="text-gray-600 mb-4">Explore nosso catálogo exclusivo de produtos em estoque com o status "BIMER". Qualidade e prontidão na entrega.</p>
                <button 
                    onClick={() => setCurrentPage('products')} 
                    className="mt-3 w-full bg-yellow-500 text-gray-900 font-bold py-2 px-4 rounded-lg hover:bg-yellow-600 transition duration-300 shadow-md"
                >
                    Ver Produtos BIMER
                </button>
            </div>
            
            <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition duration-300 border-t-4 border-green-500">
                <ShoppingCart className="w-10 h-10 text-green-600 mb-3" />
                <h2 className="text-2xl font-semibold mb-2 text-gray-800">Fácil de Comprar</h2>
                <p className="text-gray-600 mb-4">Navegue, selecione e solicite seu orçamento com facilidade. Processo simplificado para sua comodidade.</p>
                <button 
                    onClick={() => setCurrentPage('products')} 
                    className="mt-3 w-full bg-green-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-green-600 transition duration-300 shadow-md"
                >
                    Começar
                </button>
            </div>

            <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition duration-300 border-t-4 border-blue-500">
                <User className="w-10 h-10 text-blue-600 mb-3" />
                <h2 className="text-2xl font-semibold mb-2 text-gray-800">Acesso Restrito</h2>
                <p className="text-gray-600 mb-4">Área de administração para gestão de catálogo e pedidos. Acesso exclusivo para colaboradores.</p>
                <button 
                    onClick={() => setCurrentPage('login')} 
                    className="mt-3 w-full bg-blue-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-blue-600 transition duration-300 shadow-md"
                >
                    Acessar Login
                </button>
            </div>
        </div>
    </div>
);


/**
 * Página de Listagem de Produtos (Filtrada por BIMER em Estoque)
 */
const ProductsPage = ({ setNotification }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');

    const fetchProducts = useCallback(async () => {
        const MAX_RETRIES = 3;
        const BASE_DELAY_MS = 1000; // 1 segundo de atraso base
        let currentAttempt = 0;
        let lastError = null;

        setLoading(true);
        setError(null);

        while (currentAttempt < MAX_RETRIES) {
            currentAttempt++;
            try {
                const apiURL = `${API_BASE_URL}/products`;
                
                // *** LOG DE DEBUG: Verifique esta URL no seu console (F12) ***
                console.log(`[API CALL] Tentativa ${currentAttempt}: Buscando produtos em ${apiURL}`);

                // Buscando todos os produtos da API
                const response = await axios.get(apiURL);
                
                // Verifica se a resposta é válida e é um array
                if (!Array.isArray(response.data)) {
                    throw new Error("Resposta da API inválida. O formato esperado é uma lista de produtos.");
                }

                // --- INÍCIO DA LÓGICA DE FILTRAGEM (BIMER EM ESTOQUE) ---
                const filteredProducts = response.data.filter(product => {
                    // 1. O produto deve estar em estoque
                    const inStock = product.em_estoque === true;
                    
                    // 2. O produto deve estar dentro do "BIMER" (assumindo 'categoria' = 'BIMER')
                    const isBimer = product.categoria && 
                                    typeof product.categoria === 'string' &&
                                    product.categoria.toUpperCase().trim() === 'BIMER'; 
                    
                    return inStock && isBimer;
                });
                // --- FIM DA LÓGICA DE FILTRAGEM ---

                setProducts(filteredProducts);
                if (filteredProducts.length === 0) {
                    setNotification({ message: "Nenhum produto BIMER em estoque encontrado.", type: "error" });
                }
                setLoading(false);
                return; // Sucesso! Sai da função.
            } catch (err) {
                lastError = err;
                
                let errorMsg = "Erro desconhecido.";
                if (axios.isAxiosError(err)) {
                    if (err.response) {
                        // O servidor respondeu, mas com um status de erro (4xx, 5xx)
                        errorMsg = `Erro de Rota/Servidor: Status ${err.response.status} ao acessar ${API_BASE_URL}/products.`;
                    } else if (err.request) {
                        // A requisição foi feita, mas não houve resposta (Network Error)
                        errorMsg = `Erro de Conexão (Network Error). O servidor em ${API_BASE_URL} está ativo?`;
                    } else {
                        // Outros erros
                        errorMsg = `Erro na requisição: ${err.message}`;
                    }
                } else {
                    errorMsg = err.message;
                }
                
                console.error(`Tentativa ${currentAttempt} falhou ao buscar produtos:`, errorMsg, err);

                if (currentAttempt < MAX_RETRIES) {
                    // Backoff Exponencial
                    const delayTime = Math.pow(2, currentAttempt - 1) * BASE_DELAY_MS;
                    await delay(delayTime);
                }
            }
        }
        
        // Se todas as tentativas falharem
        setLoading(false);
        const finalErrorMessage = `Falha total ao carregar produtos após ${MAX_RETRIES} tentativas. Verifique a URL da API (${API_BASE_URL}/products) e o estado do backend. Detalhe: ${lastError.message || 'Erro de conexão/rota.'}`;
        setError(finalErrorMessage);
        setNotification({ message: finalErrorMessage, type: "error" });

    }, [setNotification]);

    useEffect(() => {
        fetchProducts();
    }, [fetchProducts]);

    // Filtragem por termo de busca sobre a lista já filtrada (BIMER em estoque)
    const displayedProducts = products.filter(product =>
        product.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (product.descricao && product.descricao.toLowerCase().includes(searchTerm.toLowerCase()))
    );

    if (loading) {
        return (
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 text-center">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-500 mx-auto"></div>
                <p className="mt-4 text-lg text-gray-600">Carregando produtos BIMER em estoque...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 text-center text-red-600 bg-red-50 rounded-lg shadow-md">
                <XCircle className="w-12 h-12 mx-auto mb-3" />
                <p className="text-xl font-semibold">{error}</p>
                <button onClick={fetchProducts} className="mt-4 bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded transition duration-300">Tentar Novamente</button>
            </div>
        );
    }
    
    return (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
            <h1 className="text-4xl font-extrabold text-gray-900 mb-6 border-b-4 border-yellow-500 pb-2">Catálogo de Produtos BIMER em Estoque</h1>

            <div className="mb-8">
                <div className="relative">
                    <input
                        type="text"
                        placeholder="Buscar por nome ou descrição..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="w-full p-3 pl-10 border border-gray-300 rounded-lg shadow-inner focus:ring-yellow-500 focus:border-yellow-500 transition duration-150"
                    />
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                </div>
            </div>

            {displayedProducts.length > 0 ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                    {displayedProducts.map(product => (
                        <div key={product.id} className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition duration-300 overflow-hidden transform hover:scale-[1.02] border border-gray-200">
                            <div className="p-5">
                                <h2 className="text-xl font-bold text-gray-900 mb-2">{product.nome}</h2>
                                <p className="text-sm text-gray-500 mb-3 line-clamp-2">{product.descricao || 'Sem descrição.'}</p>
                                
                                <div className="flex justify-between items-center mt-4">
                                    <span className={`px-3 py-1 text-xs font-semibold rounded-full ${product.em_estoque ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                                        {product.em_estoque ? (
                                            <>
                                                <CheckCircle className="inline-block w-3 h-3 mr-1" /> Em Estoque
                                            </>
                                        ) : (
                                            <>
                                                <XCircle className="inline-block w-3 h-3 mr-1" /> Indisponível
                                            </>
                                        )}
                                    </span>
                                    {/* Exibe o status BIMER para confirmação (já sabemos que todos são BIMER) */}
                                    <span className="px-3 py-1 text-xs font-semibold rounded-full bg-yellow-100 text-yellow-800">
                                        {product.categoria || 'BIMER'}
                                    </span>
                                </div>
                                
                                <div className="mt-4 text-center">
                                    <button
                                        onClick={() => setNotification({ message: `Solicitação de orçamento para ${product.nome} enviada!`, type: 'success' })}
                                        className="w-full bg-blue-600 text-white font-semibold py-2 rounded-lg hover:bg-blue-700 transition duration-300 shadow-md"
                                    >
                                        Solicitar Orçamento
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <div className="text-center py-10 bg-gray-50 rounded-xl shadow-inner border border-gray-200">
                    <Package className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                    <p className="text-xl text-gray-600 font-semibold">Nenhum produto BIMER em estoque encontrado com esse filtro.</p>
                </div>
            )}
        </div>
    );
};


/**
 * Página de Login
 */
const LoginPage = ({ handleLogin, setNotification, setCurrentPage }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        const MAX_RETRIES = 3;
        const BASE_DELAY_MS = 500;
        let currentAttempt = 0;
        let lastError = null;

        while (currentAttempt < MAX_RETRIES) {
            currentAttempt++;
            try {
                // Chamada à API para autenticação
                const response = await axios.post(`${API_BASE_URL}/login`, { username, password });
                
                if (response.data.success) {
                    setNotification({ message: `Bem-vindo, ${username}!`, type: 'success' });
                    handleLogin(response.data.token);
                    setCurrentPage('admin');
                    setLoading(false);
                    return; // Sucesso!
                } else {
                    // Se a falha for credencial, não precisa retentar
                    setNotification({ message: 'Credenciais inválidas.', type: 'error' });
                    setLoading(false);
                    return;
                }
            } catch (error) {
                lastError = error;
                console.error(`Tentativa ${currentAttempt} falhou no login:`, error.message);
                
                if (currentAttempt < MAX_RETRIES) {
                    const delayTime = Math.pow(2, currentAttempt - 1) * BASE_DELAY_MS;
                    await delay(delayTime);
                }
            }
        }
        
        // Se todas as tentativas falharem devido a Network Error
        setLoading(false);
        const errorMessage = `Falha ao tentar conectar após ${MAX_RETRIES} tentativas. Verifique se o backend está ativo em ${API_BASE_URL}/login.`;
        setNotification({ message: errorMessage, type: 'error' });
    };

    return (
        <div className="flex items-center justify-center min-h-screen-minus-header p-4">
            <div className="w-full max-w-md bg-white p-8 rounded-xl shadow-2xl border-t-4 border-yellow-500">
                <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">Acesso Administrativo</h2>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">Usuário</label>
                        <div className="relative">
                            <input
                                type="text"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-yellow-500 focus:border-yellow-500 pl-10"
                                required
                            />
                            <User className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                        </div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">Senha</label>
                        <div className="relative">
                            <input
                                type={showPassword ? 'text' : 'password'}
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-yellow-500 focus:border-yellow-500 pl-10 pr-10"
                                required
                            />
                            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                            <button
                                type="button"
                                onClick={() => setShowPassword(!showPassword)}
                                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600 p-1 rounded-full transition duration-150"
                            >
                                {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                            </button>
                        </div>
                    </div>
                    <div>
                        <button
                            type="submit"
                            disabled={loading}
                            className={`w-full py-3 px-4 rounded-lg text-white font-bold transition duration-300 shadow-md ${
                                loading ? 'bg-gray-500 cursor-not-allowed' : 'bg-yellow-600 hover:bg-yellow-700'
                            }`}
                        >
                            {loading ? 'Acessando...' : 'Entrar'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

/**
 * Página de Administração (AdminPage)
 * Simplesmente exibe uma mensagem se o usuário estiver logado
 */
const AdminPage = ({ isLoggedIn }) => {
    return (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
            <div className="bg-yellow-50 p-6 rounded-xl shadow-lg border-l-4 border-yellow-600">
                <h1 className="text-3xl font-bold text-gray-900 mb-4">Painel Administrativo</h1>
                {isLoggedIn ? (
                    <p className="text-lg text-gray-700">Bem-vindo à área de gestão. Aqui você pode gerenciar produtos e pedidos.</p>
                ) : (
                    <p className="text-lg text-red-600 font-semibold">Acesso negado. Por favor, faça login para acessar esta página.</p>
                )}
            </div>
        </div>
    );
};


/**
 * Componente Principal da Aplicação
 */
const App = () => {
    const [currentPage, setCurrentPage] = useState('home');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [notification, setNotification] = useState(null);

    // Efeito para limpar a notificação após 3 segundos
    useEffect(() => {
        if (notification) {
            const timer = setTimeout(() => {
                setNotification(null);
            }, 3000);
            return () => clearTimeout(timer);
        }
    }, [notification]);

    // Simulação de login
    const handleLogin = (token) => {
        // Na prática, você armazenaria o token em localStorage ou Context
        setIsLoggedIn(true);
    };

    // Simulação de logout
    const handleLogout = () => {
        setIsLoggedIn(false);
        setNotification({ message: 'Sessão encerrada com sucesso.', type: 'success' });
        setCurrentPage('home');
    };

    // Renderiza a página atual
    const renderPage = () => {
        switch (currentPage) {
            case 'products':
                return <ProductsPage setNotification={setNotification} />;
            case 'login':
                return <LoginPage handleLogin={handleLogin} setNotification={setNotification} setCurrentPage={setCurrentPage} />;
            case 'admin':
                return <AdminPage isLoggedIn={isLoggedIn} />;
            default:
                return <HomePage setCurrentPage={setCurrentPage} />;
        }
    };

    return (
        <div className="relative min-h-screen flex flex-col">
            <style jsx="true">{`
                /* Classe de utilidade para dar um espaçamento entre o header e o footer */
                .min-h-screen-minus-header {
                    min-height: calc(100vh - 120px); /* Ajustado para compensar a altura do Header e do Footer */
                }
            `}</style>

            <Header 
                currentPage={currentPage} 
                setCurrentPage={setCurrentPage} 
                isLoggedIn={isLoggedIn} 
                handleLogout={handleLogout} 
            />
            
            <main className="flex-grow">
                {renderPage()}
            </main>

            <Footer />

            {/* Componente de Notificação Flutuante */}
            {notification && (
                <div
                    className={`fixed bottom-4 right-4 p-4 rounded-lg shadow-xl text-white font-semibold transition-opacity duration-300 ${
                        notification.type === 'success' ? 'bg-green-600' : 'bg-red-600'
                    }`}
                    style={{ zIndex: 100 }} // Garante que a notificação fique acima de tudo
                >
                    {notification.message}
                </div>
            )}
        </div>
    );
};

export default App;
