const express = require('express');
const bodyParser = require('body-parser');
const cors = require('cors');
const jwt = require('jsonwebtoken');
const bcrypt = require('bcryptjs'); // Usado para encriptar e verificar senhas

// Configurações e Inicialização
const app = express();
const PORT = 3000;
// CHAVE SECRETA: Mantenha isso seguro. Usada para assinar e verificar o token JWT.
const JWT_SECRET = 'sua_chave_secreta_muito_segura'; 
const SALT_ROUNDS = 10; // Custo do hash para bcrypt

// Middleware
app.use(cors()); // Permite requisições do Frontend (porta 3001)
app.use(bodyParser.json());

// --- Banco de Dados SIMULADO (Armazenamento de usuários na memória) ---
const users = []; // Lista vazia para simular a base de usuários

// --- Middleware de Verificação de Token JWT ---
const verifyToken = (req, res, next) => {
    // 1. Obter o cabeçalho de Autorização
    const authHeader = req.headers.authorization;
    
    if (!authHeader) {
        // Se o cabeçalho não existir
        return res.status(401).json({ message: 'Acesso negado. Token não fornecido.' });
    }

    // O formato é "Bearer <token>", separamos para pegar apenas o token
    const token = authHeader.split(' ')[1];

    if (!token) {
        return res.status(401).json({ message: 'Acesso negado. Formato de token inválido.' });
    }

    try {
        // 2. Verificar e decodificar o token usando a chave secreta
        const decoded = jwt.verify(token, JWT_SECRET);
        
        // 3. Anexar os dados do usuário à requisição (para uso na rota)
        req.user = decoded; 
        
        // 4. Continuar para o endpoint /api/produtos
        next();
    } catch (err) {
        // Se a verificação falhar (token inválido/expirado)
        return res.status(403).json({ message: 'Token inválido ou expirado.' });
    }
};

// --- Rota de Registro de Usuário ---
app.post('/api/register', async (req, res) => {
    const { name, phone, email, password } = req.body;

    if (!email || !password || !name) {
        return res.status(400).json({ message: 'Nome, E-mail e Senha são obrigatórios.' });
    }

    // 1. Verificar se o usuário já existe
    if (users.find(u => u.email === email)) {
        return res.status(409).json({ message: 'E-mail já cadastrado. Use outro ou faça login.' });
    }

    try {
        // 2. Gerar o hash da senha
        const hashedPassword = await bcrypt.hash(password, SALT_ROUNDS);

        // 3. Criar e salvar o novo usuário
        const newUser = {
            id: users.length + 1,
            name,
            phone,
            email,
            password: hashedPassword // Apenas o hash é salvo
        };
        users.push(newUser);

        console.log(`Usuário registrado: ${email}`);
        return res.status(201).json({ message: 'Usuário registrado com sucesso.' });

    } catch (error) {
        console.error('Erro ao registrar usuário:', error);
        return res.status(500).json({ message: 'Erro interno do servidor ao registrar usuário.' });
    }
});

// --- Rota de Login de Usuário ---
app.post('/api/login', async (req, res) => {
    const { email, password } = req.body;

    // 1. Encontrar o usuário
    const user = users.find(u => u.email === email);

    if (!user) {
        return res.status(401).json({ message: 'Credenciais inválidas.' });
    }

    try {
        // 2. Comparar a senha fornecida com o hash salvo
        const isMatch = await bcrypt.compare(password, user.password);

        if (!isMatch) {
            return res.status(401).json({ message: 'Credenciais inválidas.' });
        }

        // 3. Gerar o Token JWT
        const token = jwt.sign(
            { id: user.id, email: user.email, name: user.name }, 
            JWT_SECRET, 
            { expiresIn: '1h' } // Token expira em 1 hora
        );

        // 4. Enviar o token e os dados do usuário (não sensíveis)
        console.log(`Login bem-sucedido para: ${email}`);
        return res.status(200).json({ 
            token, 
            user: { 
                id: user.id, 
                name: user.name, 
                email: user.email 
            } 
        });

    } catch (error) {
        console.error('Erro no login:', error);
        return res.status(500).json({ message: 'Erro interno do servidor durante o login.' });
    }
});

// --- Rota Protegida: Listagem de Produtos (simulada do Bimer) ---
// O Middleware verifyToken garante que só usuários logados acessem.
app.get('/api/produtos', verifyToken, (req, res) => {
    // Se o código chegou aqui, o token é válido.
    // req.user contém os dados do usuário logado.
    console.log(`Acesso ao Catálogo autorizado para o usuário: ${req.user.name} (${req.user.email})`);

    // Dados simulados do Bimer (Inventário)
    const mockProducts = [
        { id: 1, codigo: "PROD-B450", nome: "Processador B450", marca: "AlphaTech", preco_base: 550.00, estoque: 45 },
        { id: 2, codigo: "MEM-16GB", nome: "Memória RAM 16GB DDR4", marca: "GigaMax", preco_base: 280.50, estoque: 12 },
        { id: 3, codigo: "SSD-1TB", nome: "SSD NVMe 1TB", marca: "SpeedData", preco_base: 399.90, estoque: 5 },
        { id: 4, codigo: "VGA-RTX3060", nome: "Placa de Vídeo RTX 3060", marca: "Nvidia", preco_base: 1800.00, estoque: 0 },
        { id: 5, codigo: "MON-27IPS", nome: "Monitor LED 27 IPS 144Hz", marca: "ViewPro", preco_base: 1250.00, estoque: 28 },
    ];

    // Envia os dados de volta para o Frontend
    res.json(mockProducts);
});


// Iniciar o Servidor
app.listen(PORT, () => {
    console.log(`Backend rodando em http://localhost:${PORT}`);
});
