import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Package, DollarSign, Loader2, ArrowLeft, X, ShoppingCart, Home, BookOpen, Clock, User, Phone, Search } from 'lucide-react';

// === CONSTANTES E CONFIGURAÇÕES ===

// Paleta de Cores Ouro Branco
const GOLD_PRIMARY = '#FFA1A1A'; // Dourado (Ajustado para um amarelo mais comum para Tailwind)
const YELLOW_COLOR = '#FCD34D'; // Amarelo/Dourado (Melhor para visual)
const BLACK_PRIMARY = '#1A1A1A'; // Preto para fundo e texto principal
const WHITE_PRIMARY = '#ffffff'; // Branco

// URL do nosso servidor Node.js (Backend)
// IMPORTANT: Este é o endereço do seu backend Express.
const API_BASE_URL = 'http://localhost:3000/api';

// Funções de Utilitário
const formatCurrency = (value) => {
    // FIX: Garantir que 'value' é uma string antes de chamar 'replace'. 
    // Se for nulo, indefinido ou não-string, usamos '0' para a simulação de preço.
    const safeValue = typeof value === 'string' && value ? value : '0'; 
    
    // Simulação de preço baseado no código, já que o backend não fornece o preço
    // Usa safeValue para garantir que o replace é chamado em uma string, resolvendo o TypeError.
    const mockPrice = (parseInt(safeValue.replace(/[^0-9]/g, ''), 10) % 500) + 10;
    return new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(mockPrice);
};


// === HOOKS E LÓGICA DE DADOS ===

// Hook para buscar dados dos produtos (usando a rota /index)
const useProductData = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            setLoading(true);
            setError(null);
            try {
                // Chama a rota estática que lê o produtos-index.json
                const response = await axios.get(`${API_BASE_URL}/produtos/index`);
                
                // Assumindo a estrutura de resposta do backend: { Errors: [], ListaObjetos: [...] }
                if (response.data && response.data.ListaObjetos) {
                    setProducts(response.data.ListaObjetos);
                } else {
                    setError('Estrutura de dados inesperada da API.');
                    setProducts([]);
                }
            } catch (err) {
                console.error("Erro ao buscar catálogo de produtos:", err);
                // Detalhe de erro para o usuário verificar o backend
                setError(`Falha ao conectar ou buscar dados do catálogo. URL: ${API_BASE_URL}/produtos/index`);
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, []);

    return { products, loading, error };
};

// === COMPONENTES DA TELA ===

// 1. Navbar
const Navbar = ({ onViewChange, currentView, onSearchClick }) => {
    const navItems = [
        { name: 'Home', view: 'home', icon: Home },
        { name: 'Catálogo', view: 'catalog', icon: BookOpen },
        { name: 'Histórico', view: 'history', icon: Clock },
        { name: 'Contato', view: 'contact', icon: Phone },
        { name: 'Acesso', view: 'access', icon: User },
    ];

    return (
        <nav className="w-full bg-white shadow-lg border-t-8 border-yellow-400">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex justify-between items-center h-20">
                <div className="flex items-center space-x-4">
                    <img
                        src="https://placehold.co/40x40/1A1A1A/FFFFFF?text=OB"
                        alt="Logo Ouro Branco"
                        className="rounded-lg"
                        onError={(e) => e.target.style.display = 'none'}
                    />
                    <span className="text-xl font-extrabold text-black">OURO BRANCO</span>
                    <span className="text-xs font-semibold text-gray-500 bg-yellow-100 py-1 px-2 rounded-full hidden sm:inline">
                        Cliente: Instalação de Segurança
                    </span>
                </div>
                
                <div className="flex items-center space-x-4">
                    {navItems.map((item) => {
                        const Icon = item.icon;
                        const isActive = currentView === item.view;
                        return (
                            <button
                                key={item.view}
                                onClick={() => onViewChange(item.view)}
                                className={`flex items-center space-x-1 p-2 rounded-lg transition duration-150 ease-in-out ${
                                    isActive 
                                        ? 'text-yellow-600 border-b-2 border-yellow-600 font-bold' 
                                        : 'text-gray-600 hover:text-yellow-500 hover:bg-gray-50'
                                }`}
                            >
                                <Icon className="w-5 h-5" />
                                <span className="hidden sm:inline text-sm">{item.name}</span>
                            </button>
                        );
                    })}
                    
                    {/* Botão de Pesquisa (Nova Funcionalidade) */}
                    <button 
                        onClick={onSearchClick}
                        className="p-2 rounded-full bg-gray-200 hover:bg-gray-300 transition duration-150"
                        title="Buscar Produto por Código"
                    >
                        <Search className="w-5 h-5 text-black" />
                    </button>

                    <button className="bg-yellow-500 p-2 rounded-full shadow-lg hover:bg-yellow-600 transition duration-200">
                        <ShoppingCart className="w-6 h-6 text-black" />
                    </button>
                </div>
            </div>
        </nav>
    );
};

// 2. Search Modal (Consome /api/produtos/:codigo)
const SearchProductModal = ({ onClose }) => {
    const [codigo, setCodigo] = useState('');
    const [loading, setLoading] = useState(false);
    const [product, setProduct] = useState(null);
    const [error, setError] = useState(null);

    const handleSearch = async () => {
        if (!codigo) {
            setError("Por favor, digite um código.");
            return;
        }

        setLoading(true);
        setError(null);
        setProduct(null);

        try {
            // Chama a rota dinâmica configurada no backend: /api/produtos/:codigo
            const url = `${API_BASE_URL}/produtos/${codigo}`;
            const response = await axios.get(url);
            
            // Verifica a estrutura da resposta
            if (response.data && response.data.ListaObjetos && response.data.ListaObjetos.length > 0) {
                setProduct(response.data.ListaObjetos[0]);
            } else {
                setError(`Produto com código ${codigo} não encontrado.`);
            }

        } catch (err) {
            console.error("Erro ao buscar produto:", err);
            // Tentativa de extrair mensagem de erro do backend, se houver
            const errorMessage = err.response?.data?.Message || err.message || "Falha na comunicação com o servidor.";
            setError(`Erro ao buscar: ${errorMessage}`);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-2xl p-6 w-full max-w-lg shadow-2xl">
                <div className="flex justify-between items-center border-b pb-3 mb-4">
                    <h2 className="text-xl font-bold text-black flex items-center">
                        <Search className="w-5 h-5 mr-2 text-yellow-500" />
                        Buscar Produto por Código
                    </h2>
                    <button onClick={onClose} className="text-gray-500 hover:text-black transition">
                        <X className="w-6 h-6" />
                    </button>
                </div>

                {/* Input de Pesquisa */}
                <div className="flex space-x-2 mb-4">
                    <input
                        type="text"
                        value={codigo}
                        onChange={(e) => setCodigo(e.target.value)}
                        placeholder="Ex: 005072 ou 006180"
                        className="flex-grow p-3 border border-gray-300 rounded-lg focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition"
                    />
                    <button
                        onClick={handleSearch}
                        disabled={loading}
                        className={`p-3 rounded-lg text-white font-semibold flex items-center transition duration-200 ${
                            loading ? 'bg-gray-400' : 'bg-black hover:bg-gray-800'
                        }`}
                    >
                        {loading ? <Loader2 className="w-5 h-5 animate-spin mr-2" /> : <Search className="w-5 h-5 mr-2" />}
                        Buscar
                    </button>
                </div>

                {/* Área de Resultados */}
                {error && (
                    <div className="p-3 bg-red-100 border border-red-400 text-red-700 rounded-lg text-sm mb-4">
                        <p className="font-bold">Erro / Não Encontrado:</p>
                        <p>{error}</p>
                        <p className="mt-2 text-xs">A rota dinâmica `/api/produtos/:codigo` foi chamada.</p>
                    </div>
                )}
                
                {product && (
                    <div className="bg-yellow-50 p-4 rounded-xl border border-yellow-300">
                        <h3 className="text-xl font-bold mb-1">{product.nome}</h3>
                        <p className="text-sm text-gray-700 mb-3">
                            Código: <span className="font-mono text-black font-semibold">{product.codigo}</span>
                        </p>
                        <div className="flex justify-between items-center pt-2 border-t border-yellow-200">
                            <span className="text-2xl font-extrabold text-green-700">
                                {formatCurrency(product.codigo)} {/* Preço mockado */}
                            </span>
                            <span className="text-sm text-gray-500">
                                Identificador: {product.Identificador}
                            </span>
                        </div>
                    </div>
                )}

                {!loading && !product && !error && (
                    <p className="text-center text-gray-500 pt-4">Digite o código do produto e clique em buscar.</p>
                )}
            </div>
        </div>
    );
};


// 3. HomeScreen (Baseado na imagem fdf763.png)
const HomeScreen = ({ onViewChange }) => (
    <div className="p-10 bg-white shadow-2xl rounded-2xl w-full max-w-2xl text-center border-t-8 border-yellow-400">
        <h1 className="text-4xl sm:text-5xl font-extrabold mb-4">
            Bem-Vindo à <span className="text-yellow-500">OURO</span> <span className="text-yellow-500">BRANCO</span>
        </h1>
        <p className="text-gray-600 text-lg mb-8">
            Sua parceira em materiais e equipamentos de alta qualidade.
        </p>
        <div className="flex flex-col sm:flex-row justify-center space-y-4 sm:space-y-0 sm:space-x-4">
            <button
                onClick={() => onViewChange('catalog')}
                className="flex items-center justify-center bg-black text-white font-semibold py-3 px-6 rounded-xl shadow-lg hover:bg-gray-800 transition duration-200"
            >
                <BookOpen className="w-5 h-5 mr-2" />
                Explorar Catálogo de Grupos
            </button>
            <button
                onClick={() => onViewChange('access')}
                className="flex items-center justify-center border border-black text-black font-semibold py-3 px-6 rounded-xl shadow-md hover:bg-gray-50 transition duration-200"
            >
                <User className="w-5 h-5 mr-2" />
                Sou um(a) Instalação de Segurança
            </button>
        </div>
        <p className="text-sm text-gray-400 mt-6">
            Acesse o catálogo para ver preços e produtos exclusivos para o seu segmento.
        </p>
    </div>
);

// 4. CatalogScreen (Consome /api/produtos/index)
const CatalogScreen = ({ onViewChange }) => {
    const { products, loading, error } = useProductData();

    return (
        <div className="w-full max-w-6xl p-6 bg-white shadow-2xl rounded-2xl border-t-8 border-yellow-400">
            <div className="flex items-center justify-between mb-6 border-b pb-3">
                <h2 className="text-3xl font-bold text-black">Catálogo de Produtos (Index)</h2>
                <button
                    onClick={() => onViewChange('home')}
                    className="flex items-center text-blue-600 hover:text-blue-800 transition duration-150"
                >
                    <ArrowLeft className="w-4 h-4 mr-1" /> Voltar
                </button>
            </div>

            {loading && (
                <div className="flex flex-col items-center justify-center p-10 text-gray-500">
                    <Loader2 className="w-8 h-8 animate-spin mb-3 text-yellow-500" />
                    <p>Carregando dados do backend... ({API_BASE_URL}/produtos/index)</p>
                </div>
            )}

            {error && (
                <div className="p-6 bg-red-100 border border-red-400 text-red-700 rounded-lg">
                    <p className="font-bold">Erro ao carregar o Catálogo:</p>
                    <p className="text-sm">{error}</p>
                    <p className="mt-2 text-xs">Verifique se o seu servidor Node.js/Express está rodando na porta 3000 e se a rota `/api/produtos/index` está configurada corretamente e retornando o `produtos-index.json`.</p>
                </div>
            )}

            {!loading && products.length > 0 && (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {products.slice(0, 15).map((product, index) => ( // Limita a 15 para demonstração
                        <div key={index} className="bg-gray-50 p-4 rounded-xl border border-gray-200 hover:shadow-md transition duration-200">
                            <h3 className="text-lg font-semibold text-black mb-1">{product.nome}</h3>
                            <p className="text-sm text-gray-500">
                                Código: <span className="font-mono text-gray-700 font-bold">{product.codigo}</span>
                            </p>
                            <div className="flex justify-between items-center mt-3 pt-3 border-t border-gray-200">
                                <span className="text-xl font-bold text-green-700">
                                    {formatCurrency(product.codigo)} {/* Preço mockado */}
                                </span>
                                <button className="flex items-center bg-yellow-500 text-black py-1 px-3 rounded-lg text-sm font-medium hover:bg-yellow-600 transition duration-150">
                                    Detalhes
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
            {!loading && !error && products.length === 0 && (
                <div className="p-6 text-center text-gray-500">
                    Nenhum produto encontrado. A lista `ListaObjetos` está vazia.
                </div>
            )}
        </div>
    );
};

// 5. Access Screen (Tela de Acesso/Login simplificada)
const AccessScreen = ({ onViewChange }) => (
    <div className="p-8 bg-white shadow-2xl rounded-2xl w-full max-w-md text-center border-t-8 border-yellow-400">
        <h2 className="text-2xl font-bold text-black mb-4">Acesso de Clientes</h2>
        <p className="text-gray-600 mb-6">Esta é uma tela de acesso simulada para autenticação.</p>
        <button
            onClick={() => onViewChange('home')}
            className="flex items-center justify-center w-full bg-black text-white py-3 rounded-lg font-semibold hover:bg-gray-800 transition duration-200"
        >
            <ArrowLeft className="w-4 h-4 mr-2" /> Voltar para Home
        </button>
    </div>
);


// === COMPONENTE PRINCIPAL (App) ===
const App = () => {
    // Gerenciamento de visualização (simula o roteamento)
    const [view, setView] = useState('home'); 
    const [showSearchModal, setShowSearchModal] = useState(false);

    const handleViewChange = useCallback((newView) => {
        setView(newView);
    }, []);

    // Função para renderizar o conteúdo central
    const renderContent = () => {
        switch (view) {
            case 'home':
                return <HomeScreen onViewChange={handleViewChange} />;
            case 'catalog':
                return <CatalogScreen onViewChange={handleViewChange} />;
            case 'access':
                return <AccessScreen onViewChange={handleViewChange} />;
            case 'history':
            case 'contact':
                return <div className="p-10 text-xl font-semibold text-gray-600">Página em Construção: {view.toUpperCase()}</div>
            default:
                return <HomeScreen onViewChange={handleViewChange} />;
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center font-sans">
            <Navbar 
                onViewChange={handleViewChange} 
                currentView={view} 
                onSearchClick={() => setShowSearchModal(true)} 
            />
            
            <main className="flex-grow flex items-center justify-center p-4 w-full">
                {renderContent()}
            </main>

            <footer className="w-full bg-gray-800 p-4 text-center text-gray-400 text-sm mt-auto">
                © 2025 Ouro Branco. Todos os direitos reservados.
            </footer>

            {/* Renderiza o Modal de Pesquisa se showSearchModal for true */}
            {showSearchModal && <SearchProductModal onClose={() => setShowSearchModal(false)} />}
        </div>
    );
};

export default App;
