// ConfiguraÃ§Ãµes bÃ¡sicas de layout
const PRIMARY_COLOR = 'bg-yellow-500';
const HOVER_COLOR = 'hover:bg-yellow-600';

// --- MOCK DE DADOS/HOOKS PARA AMBIENTE DE DEMONSTRAÃ‡ÃƒO ---

// Mock do Hook de AutenticaÃ§Ã£o (useBimerAuth)
// Este hook simula o comportamento de autenticaÃ§Ã£o e dados de revendedor (resellerData)
// O erro original foi corrigido aqui, garantindo que o retorno inicial nÃ£o seja null,
// ou que o componente chamador trate o null. Implementaremos o tratamento na App.
const useBimerAuth = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [user, setUser] = useState(null);
    const [resellerData, setResellerData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simula o carregamento da autenticaÃ§Ã£o
        const timer = setTimeout(() => {
            // SimulaÃ§Ã£o de usuÃ¡rio logado como revendedor
            const mockUser = { id: 'user-123', name: 'JoÃ£o Silva', email: 'joao.reseller@example.com' };
            const mockResellerData = {
                id: 'reseller-001',
                name: 'Revenda Ouro Total',
                commissionRate: 0.15,
                clients: 42,
            };

            // VocÃª pode comentar as 3 linhas abaixo para simular um usuÃ¡rio deslogado
            setIsLoggedIn(true);
            setUser(mockUser);
            setResellerData(mockResellerData);

            setLoading(false);
        }, 1500);

        return () => clearTimeout(timer);
    }, []);

    const handleLogin = (credentials) => {
        setLoading(true);
        // SimulaÃ§Ã£o de API call
        setTimeout(() => {
            const mockUser = { id: 'user-123', name: 'JoÃ£o Silva', email: credentials.email };
            const mockResellerData = {
                id: 'reseller-001',
                name: 'Revenda Ouro Total',
                commissionRate: 0.15,
                clients: 42,
            };
            setIsLoggedIn(true);
            setUser(mockUser);
            setResellerData(mockResellerData);
            setLoading(false);
        }, 1000);
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUser(null);
        setResellerData(null);
    };

    return { 
        isLoggedIn, 
        user, 
        resellerData, 
        loading,
        handleLogin, 
        handleLogout 
    };
};

