 */
const Input = ({ label, type = 'text', value, onChange, placeholder, icon: Icon, required = false, isPassword, toggleVisibility }) => (
    <div className="flex flex-col space-y-1">
        <label className="text-sm font-medium text-gray-700">{label}</label>
        <div className="relative flex items-center">
            {Icon && <Icon className="absolute left-3 w-5 h-5 text-gray-400" />}
            <input
                type={type}
                value={value}
                onChange={onChange}
                placeholder={placeholder}
                required={required}
                className={`w-full p-3 pl-10 pr-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-yellow-500 focus:border-yellow-500 transition duration-150`}
            />
            {isPassword && (
                <button 
                    type="button"
                    onClick={toggleVisibility}
                    className="absolute right-3 p-1 text-gray-500 hover:text-gray-700 transition"
                >
                    {type === 'password' ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                </button>
            )}
        </div>
    </div>
);

