                </div>

                <div className="flex items-center space-x-3">
                    {isLoggedIn ? (
                        <button
                            onClick={handleLogout}
                            className="flex items-center bg-red-600 text-white text-sm font-medium p-2 rounded-full hover:bg-red-700 transition duration-300 shadow-md"
