/**
 * PÃ¡gina de Login
 */
const LoginPage = ({ handleLogin, setCurrentPage, setNotification }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            // SimulaÃ§Ã£o de chamada de login
            await new Promise(resolve => setTimeout(resolve, 1500));
            handleLogin({ email, password });
            setNotification({ message: 'Login realizado com sucesso! Redirecionando...', type: 'success' });
            setCurrentPage('reseller-dashboard');
        } catch (error) {
            setNotification({ message: 'Falha no login. Verifique suas credenciais.', type: 'error' });
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-md mx-auto py-12 px-4">
            <div className="bg-white p-8 rounded-xl shadow-2xl">
                <h2 className="text-3xl font-bold text-gray-900 mb-6 text-center">Acesso do Revendedor</h2>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <Input
                        label="E-mail"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="seu.email@exemplo.com"
                        icon={User}
                        required
                    />
                    <Input
                        label="Senha"
                        type={showPassword ? 'text' : 'password'}
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Sua senha secreta"
                        icon={Lock}
                        required
                        isPassword
                        toggleVisibility={() => setShowPassword(!showPassword)}
                    />

                    <button
                        type="submit"
                        disabled={loading}
                        className={`w-full py-3 text-lg font-semibold text-white rounded-full transition duration-300 shadow-md flex items-center justify-center ${
                            loading ? 'bg-gray-400 cursor-not-allowed' : `${PRIMARY_COLOR} ${HOVER_COLOR}`
                        }`}
                    >
                        {loading ? (
                            <>
                                <Loader2 className="w-5 h-5 mr-2 animate-spin" /> Entrando...
                            </>
                        ) : (
                            <>
                                <LogIn className="w-5 h-5 mr-2" /> Entrar
                            </>
                        )}
                    </button>
                </form>
                <p className="mt-4 text-center text-sm text-gray-500">
                    Ainda nÃ£o Ã© revendedor? <span className="text-yellow-600 font-medium cursor-pointer hover:underline">Fale conosco</span>
                </p>
            </div>
        </div>
    );
};


