};


/**
 * Componente Principal da AplicaÃ§Ã£o
 */
const App = () => {
    // Estados da AplicaÃ§Ã£o
    const [currentPage, setCurrentPage] = useState('home');
    const [notification, setNotification] = useState(null); // NotificaÃ§Ã£o flutuante

    // Hook de AutenticaÃ§Ã£o
    // A CORREÃ‡ÃƒO CRÃTICA estÃ¡ na desestruturaÃ§Ã£o, que usa `useBimerAuth() || {}` para evitar a desestruturaÃ§Ã£o de `null`.
    const authContext = useBimerAuth();
    
    // Garantir que authContext nÃ£o Ã© null antes de desestruturar
    // Adicionamos valores padrÃ£o para todas as propriedades para robustez
    const { 
        isLoggedIn = false, 
        user = null, 
        handleLogin = () => {}, 
        handleLogout = () => {}, 
        resellerData = null,
        loading = true // Adicionando loading para gerenciar o estado inicial
    } = authContext || {}; 


    // Efeito para limpar a notificaÃ§Ã£o automaticamente
    useEffect(() => {
        if (notification) {
            const timer = setTimeout(() => {
                setNotification(null);
            }, 3000);
            return () => clearTimeout(timer);
        }
    }, [notification]);

    /**
     * FunÃ§Ã£o que renderiza o componente da pÃ¡gina atual
     */
    const renderPage = () => {
        if (loading) {
            return (
                <div className="flex justify-center items-center min-h-screen-minus-header">
                    <Loader2 className="w-12 h-12 text-yellow-500 animate-spin" />
                    <span className="ml-4 text-lg text-gray-600">Carregando aplicaÃ§Ã£o...</span>
                </div>
            );
        }
        
        // Define as rotas
        switch (currentPage) {
            case 'products':
                return <ProductsPage />;
            case 'contact':
                return <ContactPage />;
            case 'login':
                // Passamos o setNotification para o componente de Login
                return <LoginPage handleLogin={handleLogin} setCurrentPage={setCurrentPage} setNotification={setNotification} />;
            case 'reseller-dashboard':
                // Garante que a pÃ¡gina sÃ³ renderize se estiver logado E se houver dados do revendedor
                if (isLoggedIn && resellerData) {
                    return <ResellerDashboardPage resellerData={resellerData} user={user} />;
                }
                // Redireciona para o login se nÃ£o estiver autorizado ou dados nÃ£o carregados
                return <LoginPage handleLogin={handleLogin} setCurrentPage={setCurrentPage} setNotification={setNotification} />;
            default:
                return <HomePage setCurrentPage={setCurrentPage} />;
        }
    };

    return (
        <div className="relative min-h-screen flex flex-col font-sans">
            <script src="https://cdn.tailwindcss.com"></script>
            <link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700;800&display=swap" rel="stylesheet" />

            {/* Estilos globais e de utilidade */}
            <style>
                {`
                    body {
                        font-family: 'Inter', sans-serif;
                        background-color: #f3f4f6;
                    }
                    /* Classe de utilidade para dar um espaÃ§amento entre o header e o footer */
                    .min-h-screen-minus-header {
                        min-height: calc(100vh - 120px); /* Ajustado para compensar a altura do Header e do Footer */
                    }
                `}
            </style>

            <Header 
                currentPage={currentPage} 
                setCurrentPage={setCurrentPage} 
                isLoggedIn={isLoggedIn} 
                handleLogout={handleLogout} 
            />
            
            <main className="flex-grow">
                {renderPage()}
            </main>

            <Footer />

            {/* Componente de NotificaÃ§Ã£o Flutuante */}
            {notification && (
                <div
                    className={`fixed bottom-4 right-4 p-4 rounded-lg shadow-xl text-white font-semibold transition-opacity duration-300 transform motion-reduce:transition-none ${
                        notification.type === 'success' ? 'bg-green-600' : 'bg-red-600'
                    }`}
                    style={{ zIndex: 100 }} // Garante que a notificaÃ§Ã£o fique acima de tudo
                >
                    {notification.message}
                </div>
            )}
        </div>
    );
};

export default App;
