// URL base para o backend (ajuste se a porta mudar)
// ATENÇÃO: Mudança de 'localhost' para o IP do servidor para comunicação interna.
const API_BASE_URL = 'http://192.168.5.12:3000/api';

// --- COMPONENTES DA PÁGINA ---

/**
 * Componente de Cabeçalho (Navbar)
 */
const Header = ({ currentPage, setCurrentPage, isLoggedIn, handleLogout }) => {
    // Retorna a cor do texto baseada na página ativa
    const getLinkClass = (page) =>
        `text-white text-sm font-medium p-2 rounded-lg transition duration-300 ${
            currentPage === page ? 'bg-yellow-600' : 'hover:bg-yellow-700'
        }`;

    return (
        <header className="bg-gray-900 shadow-md">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-3 flex justify-between items-center">
                <div className="flex items-center space-x-4">
                    <span className="text-xl font-bold text-yellow-500 tracking-wider">
                        Portal Ouro Branco
                    </span>
                    <nav className="hidden md:flex space-x-4">
                        <button onClick={() => setCurrentPage('home')} className={getLinkClass('home')}>
                            <HomeIcon size={18} className="inline mr-1" /> Início
                        </button>
                        <button onClick={() => setCurrentPage('catalog')} className={getLinkClass('catalog')}>
                            <Package size={18} className="inline mr-1" /> Catálogo
                        </button>
                        <button onClick={() => setCurrentPage('search')} className={getLinkClass('search')}>
                            <Search size={18} className="inline mr-1" /> Busca
                        </button>
                        {isLoggedIn && (
                             <button onClick={() => setCurrentPage('admin')} className={getLinkClass('admin')}>
                                <Lock size={18} className="inline mr-1" /> Admin
                            </button>
                        )}
                        <button onClick={() => setCurrentPage('contact')} className={getLinkClass('contact')}>
                            <Phone size={18} className="inline mr-1" /> Contato
                        </button>
                    </nav>
                </div>

                <div className="flex items-center space-x-4">
                    <button onClick={() => setCurrentPage('cart')} className={getLinkClass('cart')}>
                        <ShoppingCart size={18} className="inline mr-1" /> Carrinho (0)
                    </button>
                    {isLoggedIn ? (
                        <>
                            <button onClick={() => setCurrentPage('profile')} className={getLinkClass('profile')}>
                                <User size={18} className="inline mr-1" /> Perfil
                            </button>
                            <button onClick={handleLogout} className="text-sm font-medium p-2 rounded-lg transition duration-300 bg-red-600 hover:bg-red-700 text-white">
                                Sair
                            </button>
                        </>
                    ) : (
                        <button onClick={() => setCurrentPage('login')} className={getLinkClass('login')}>
                            <LogIn size={18} className="inline mr-1" /> Login
                        </button>
                    )}
                </div>
            </div>
        </header>
    );
};

/**
 * Componente de Rodapé
 */
const Footer = () => (
    <footer className="bg-gray-800 text-white py-4 mt-auto">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center text-sm">
            &copy; {new Date().getFullYear()} Portal Ouro Branco. Todos os direitos reservados.
        </div>
    </footer>
);

/**
 * Componente da Página Inicial
 */
const HomePage = ({ setCurrentPage }) => (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 text-center">
        <h1 className="text-5xl font-extrabold text-gray-800 mb-4">Bem-vindo ao Portal Ouro Branco</h1>
        <p className="text-xl text-gray-600 mb-8">Sua plataforma completa para gerenciamento e pedidos.</p>
        <div className="flex justify-center space-x-4">
            <button
                onClick={() => setCurrentPage('catalog')}
                className="bg-yellow-500 hover:bg-yellow-600 text-white font-bold py-3 px-6 rounded-lg shadow-lg transition duration-300 transform hover:scale-105"
            >
                Explorar Catálogo
            </button>
            <button
                onClick={() => setCurrentPage('login')}
                className="bg-gray-700 hover:bg-gray-800 text-white font-bold py-3 px-6 rounded-lg shadow-lg transition duration-300 transform hover:scale-105"
            >
                Fazer Login
            </button>
        </div>
    </div>
);

/**
 * Componente da Página de Login
 */
const LoginPage = ({ setCurrentPage, setLoggedIn, setNotification }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        try {
            const response = await axios.post(`${API_BASE_URL}/auth/login`, { email, password });
            
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                setLoggedIn(true);
                setCurrentPage('home');
                setNotification({ message: 'Login realizado com sucesso!', type: 'success' });
            } else {
                setNotification({ message: 'Resposta de login inesperada.', type: 'error' });
            }
        } catch (error) {
            console.error("Erro no login:", error);
            const errorMessage = error.response?.data?.message || 'Email ou senha inválidos.';
            setNotification({ message: errorMessage, type: 'error' });
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="max-w-md mx-auto my-12 p-8 bg-white shadow-2xl rounded-xl">
            <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Acesso ao Sistema</h2>
            <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="email">Email</label>
                    <input
                        id="email"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-yellow-500 focus:border-yellow-500 transition duration-150"
                        placeholder="seu@email.com"
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="password">Senha</label>
                    <div className="relative">
                        <input
                            id="password"
                            type={showPassword ? 'text' : 'password'}
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="w-full px-4 py-2 border border-gray-300 rounded-lg pr-10 focus:ring-yellow-500 focus:border-yellow-500 transition duration-150"
                            placeholder="Sua senha secreta"
                        />
                         <button
                            type="button"
                            onClick={() => setShowPassword(!showPassword)}
                            className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-500 hover:text-gray-700"
                            aria-label={showPassword ? 'Ocultar senha' : 'Mostrar senha'}
                        >
                            {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                        </button>
                    </div>
                </div>
                <button
                    type="submit"
                    disabled={isLoading}
                    className="w-full bg-yellow-500 hover:bg-yellow-600 text-white font-bold py-2 px-4 rounded-lg shadow-md transition duration-300 disabled:opacity-50 flex items-center justify-center"
                >
                    {isLoading ? (
                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                        </svg>
                    ) : (
                        'Entrar'
                    )}
                </button>
            </form>
        </div>
    );
};

// Placeholder para outras páginas (Admin, Catalog, Search, Profile, Cart, Contact)
const PlaceholderPage = ({ title }) => (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 text-center">
        <h1 className="text-4xl font-bold text-gray-800 mb-4">{title}</h1>
        <p className="text-lg text-gray-600">Conteúdo da página {title} em construção.</p>
    </div>
);

/**
 * Componente Principal da Aplicação
 */
const App = () => {
    const [currentPage, setCurrentPage] = useState('home');
    const [isLoggedIn, setLoggedIn] = useState(false);
    const [notification, setNotification] = useState(null); // { message: string, type: 'success' | 'error' }

    // Efeito para limpar a notificação após 3 segundos
    useEffect(() => {
        if (notification) {
            const timer = setTimeout(() => {
                setNotification(null);
            }, 3000);
            return () => clearTimeout(timer);
        }
    }, [notification]);

    // Verifica o token ao carregar o app
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            // Aqui, em um app real, você faria uma chamada para validar o token
            setLoggedIn(true);
        }
    }, []);

    const handleLogout = useCallback(() => {
        localStorage.removeItem('token');
        setLoggedIn(false);
        setCurrentPage('home');
        setNotification({ message: 'Logout realizado com sucesso.', type: 'success' });
    }, []);

    const renderPage = () => {
        switch (currentPage) {
            case 'login':
                return <LoginPage setCurrentPage={setCurrentPage} setLoggedIn={setLoggedIn} setNotification={setNotification} />;
            case 'admin':
                return isLoggedIn ? <PlaceholderPage title="Área Administrativa" /> : <LoginPage setCurrentPage={setCurrentPage} setLoggedIn={setLoggedIn} setNotification={setNotification} />;
            case 'catalog':
                return <PlaceholderPage title="Catálogo de Produtos" />;
            case 'search':
                return <PlaceholderPage title="Busca Avançada" />;
            case 'profile':
                return isLoggedIn ? <PlaceholderPage title="Meu Perfil" /> : <LoginPage setCurrentPage={setCurrentPage} setLoggedIn={setLoggedIn} setNotification={setNotification} />;
            case 'cart':
                return <PlaceholderPage title="Meu Carrinho" />;
            case 'contact':
                return <PlaceholderPage title="Contato e Suporte" />;
            default:
                return <HomePage setCurrentPage={setCurrentPage} />;
        }
    };

    return (
        <div className="relative min-h-screen flex flex-col">
            <style jsx="true">{`
                /* Classe de utilidade para dar um espaçamento entre o header e o footer */
                .min-h-screen-minus-header {
                    min-height: calc(100vh - 120px); /* Ajustado para compensar a altura do Header e do Footer */
                }
            `}</style>

            <Header 
                currentPage={currentPage} 
                setCurrentPage={setCurrentPage} 
                isLoggedIn={isLoggedIn} 
                handleLogout={handleLogout} 
            />
            
            <main className="flex-grow">
                {renderPage()}
            </main>

            <Footer />

            {/* Componente de Notificação Flutuante */}
            {notification && (
                <div
                    className={`fixed bottom-4 right-4 p-4 rounded-lg shadow-xl text-white font-semibold transition-opacity duration-300 ${
                        notification.type === 'success' ? 'bg-green-600' : 'bg-red-600'
                    }`}
                    style={{ zIndex: 100 }} // Garante que a notificação fique acima de tudo
                >
                    {notification.message}
                </div>
            )}
        </div>
    );
};

export default App;
