import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Home, UserPlus, Lock, LogOut } from 'lucide-react';

const API_BASE_URL = 'https://seu-backend-api.com'; // üîß altere para o endpoint real do seu backend

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [notification, setNotification] = useState(null);

  const showNotification = (message, type = 'info') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) setIsLoggedIn(true);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    localStorage.removeItem('nome');
    setIsLoggedIn(false);
    setCurrentPage('home');
    showNotification('Logout realizado com sucesso.', 'success');
  };

  const getLinkClass = (page) =>
    `px-3 py-2 rounded-md text-sm font-medium ${
      currentPage === page ? 'bg-blue-600 text-white' : 'text-gray-700 hover:bg-gray-200'
    }`;

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <HomePage />;
      case 'register':
        return <RegisterPage showNotification={showNotification} />;
      case 'login':
        return <LoginPage setIsLoggedIn={setIsLoggedIn} setCurrentPage={setCurrentPage} showNotification={showNotification} />;
      case 'intranet':
        return <IntranetPage showNotification={showNotification} />;
      default:
        return <HomePage />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Header
        isLoggedIn={isLoggedIn}
        handleLogout={handleLogout}
        setCurrentPage={setCurrentPage}
        getLinkClass={getLinkClass}
      />
      <main className="p-6">{renderPage()}</main>
      {notification && (
        <div
          className={`fixed bottom-4 right-4 px-4 py-2 rounded shadow-lg text-white ${
            notification.type === 'success'
              ? 'bg-green-500'
              : notification.type === 'error'
              ? 'bg-red-500'
              : 'bg-blue-500'
          }`}
        >
          {notification.message}
        </div>
      )}
    </div>
  );
};

const Header = ({ isLoggedIn, handleLogout, setCurrentPage, getLinkClass }) => (
  <header className="bg-white shadow-md">
    <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
      <h1 className="text-2xl font-bold text-blue-700">Ouro Branco</h1>
      <nav className="space-x-2">
        <button onClick={() => setCurrentPage('home')} className={getLinkClass('home')}>
          <Home className="w-4 h-4 inline mr-1" /> In√≠cio
        </button>
        <button onClick={() => setCurrentPage('register')} className={getLinkClass('register')}>
          <UserPlus className="w-4 h-4 inline mr-1" /> Cadastro
        </button>
        {!isLoggedIn ? (
          <button onClick={() => setCurrentPage('login')} className={getLinkClass('login')}>
            <Lock className="w-4 h-4 inline mr-1" /> Login
          </button>
        ) : (
          <>
            <button onClick={() => setCurrentPage('intranet')} className={getLinkClass('intranet')}>
              <Lock className="w-4 h-4 inline mr-1" /> Portal Interno
            </button>
