// üîê P√°gina de Login
const LoginPage = ({ setIsLoggedIn, setCurrentPage, showNotification }) => {
  const [cnpj, setCnpj] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${API_BASE_URL}/login`, { cnpj, password });
      if (response.data && response.data.token) {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('role', response.data.role);
        localStorage.setItem('nome', response.data.nome);
        setIsLoggedIn(true);
        showNotification('Login realizado com sucesso!', 'success');
        setCurrentPage('intranet');
      }
    } catch {
      showNotification('Falha no login. Verifique as credenciais.', 'error');
    }
  };

  return (
    <div className="max-w-sm mx-auto bg-white p-6 rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Acesso ao Sistema</h2>
      <form onSubmit={handleLogin} className="space-y-4">
        <input
          type="text"
          placeholder="CNPJ"
          value={cnpj}
          onChange={(e) => setCnpj(e.target.value)}
          className="w-full border p-2 rounded"
          required
        />
        <input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full border p-2 rounded"
          required
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Entrar
        </button>
      </form>
    </div>
  );
};

