            <h2 className="text-2xl font-bold mb-4 text-gray-800">Minhas Comiss√µes</h2>
            <p className="text-gray-700 mb-4">Voc√™ cadastrou {minhasVendas.length} clientes.</p>
            <p className="text-yellow-600 font-bold">
              Comiss√£o total estimada: R$ {(minhasVendas.length * 50).toFixed(2)}
            </p>
          </div>
        );

      case 'gerente':
        return (
          <div className="bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-bold mb-4 text-gray-800">Painel do Gerente</h2>
