  if (loading) return <p className="text-center text-gray-500 py-10">Carregando...</p>;

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6 border-b pb-2">Portal Interno Ouro Branco</h1>
